style.css:
@-moz-document domain(boardgamegeek.com) {
	/* Remove Adverts */
	#dfp-leaderboard {
		display: none;
	}
	#dfp-button {
		display: none;
	}
	.mb10 {
		display: none;
	}
	#dfp-skyscraper {
		display: none;
	}
	
	/* Remove right sidebar ad */
	#maincontent > table:nth-child(2) > tbody > tr > td:nth-child(2) > table > tbody > tr > td:nth-child(2) {
		display: none;
	}
	
	/* Hide BGG logo */
	#maincontent > table:first-child > tbody > tr > td:first-child {
		display: none;
	}
	
	#maincontent > table:first-child > tbody > tr > td:nth-child(2) {
		padding-left: 0 !important;
	}
	
	/* Hide left sidebar */
	#maincontent > table:nth-child(2) > tbody > tr > td:first-child {
		display: none;
	}
	
	#maincontent > table:nth-child(2) > tbody > tr > td:nth-child(2) {
		padding-left: 0 !important;
	}

}
