style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnbeta.com") {

	/* new wide style for cnbeta 2013-06-24  */
	#mask{height:0px; width:0px; z-index: -10000; position: absolute; top: -100000px; left: -100000px;}
	#mask *{display: none !important;}
	iframe{display: none;}
	.wrapper>section:first-child{display: none;}
	.wrapper+script+div[id], .wrapper+div[id], iframe+div[id]{position: absolute; z-index: -10000; top: -1000000px; left: -1000000px; visibility: hidden;}
	.article .body{display: block !important; visibility: visible !important;}
	.content_box .tal{display: none;}
	#concern_box + .mt5{display: none;}
	.article_content + *, .article_content + * + *{display: none;}
	.commt-r{display: none !important;}
	#main-commentbox + *{display: none;}
	ins{display: none;}
	.cooperation{display: none;}
	.main_content + div{display: none;}
	.global_head{display: none !important;}
	.slide{display: none !important;}
	.headline{float: none !important; width: auto !important; padding-left: 0px !important; margin:0 !important;}
	.headline .top_text{right: auto !important; left: 120px; }
	.headline .top_img{height: auto !important; position: absolute; left: -90px; top: -24px;}
	.headline dt{margin-bottom: 0px !important; }
	.headline dd{}
	.headline dl{height: auto !important; }
	.headline_area{height: auto !important;}
        #concern_switch{display:none;}
	.cb_box{height: auto !important; padding: 5px !important;}
	.cb_box +section{display: none !important;}
	.imgbox85x62{display: block !important; }
	.main_content_left{width: 100% !important;}
	.main_content_left >section:nth-child(4){display: none !important;}
	.main_content_right{display:  none !important; }

	.blue_bar{background: #538AC1 !important;}
	.focus{display: none;}
	.realtime{float: none !important; width: auto !important; padding-left: 0px !important; height:auto !important;}
	.realtime_list dl{padding: 0px !important;}
	.realtime .brief{right:100px !important;}
	.all_news_wildlist dl{width:  auto !important; margin:0 5px !important; padding: 5px 10px !important;  float: none !important; display: block !important; min-height: 110px; }
	.all_news_wildlist dl:hover{box-shadow: 0 0 5px rgba(0,0,0,0.2);}
	.all_news_wildlist dt{margin-bottom: 0px !important; padding-bottom:0px !important; }
        .all_news_wildlist dt p{display:none !important;}
        .all_news_wildlist dd{overflow:inherit !important;}
	.all_news_wildlist dd:before, .all_news_wildlist dd:after{content: " "; display: block;  height: 0; visibility: hidden;}
	.all_news_wildlist dd:after{clear: both;}
        .all_news_wildlist dl dd:nth-child(3){}
        .all_news_wildlist .tj{float:right;}
        .all_news_wildlist .newsinfo{ margin-left: 120px; display:block;}
	.all_news_wildlist .read{top: -28px !important;}
	.all_news_wildlist .tools{}
        .all_news_wildlist .tools .time{display:none;}
        .all_news_wildlist .picno{float: left !important; margin: -30px 0 0 0 !important;}
        .all_news_wildlist .pic{margin:0 !important;}
        .all_news_wildlist .title{float:none !important; width: auto !important; padding-left: 120px !important;}
	.clear{height: 0px !important;}

	.read_more .more{ background: #F5F5F5 !important;}
	.items_area{overflow: hidden;}
	.white_bottom{display: none;}
	.copyright{display: none !important;}
	.main_content +section, #googleAd_afc, .commt_r{display: none !important;}

	/* content center */
	.article .body .content{text-align: center;}
	.article .body .content img{display: block; margin: 0 auto !important;}
        .cbv{display:none;}
        .navi .bd .bd_r{display:none !important;}
        .bfd_img_logo{display:none !important;}
        .J_emotion{display:none;}
}
