@-moz-document domain("hani.co.kr"), domain("hani.com") {

* {
    background-color: #000 !important;
    border-color: #262626 !important;
    color: #9a9a9a !important;
}
a {
    color: #000000 !important;
}
a:visited {
    color: #ccc !important;
}
a:link {
    color: #ccc !important;
    /*font-weight: bold !important;*/
}
a:hover,
a:hover span {
    color: #fff !important;
    text-decoration: underline !important;
}
#main-top {
    background: inherit;
}
#header,
#footer,
#sitemap,
.title,
.tab-menu,
.theme-title,
.gnb-inner,
.btn.prev,
.btn.next,
