style.css:
@-moz-document 
url-prefix('http://twitter.com/') {

textarea#status {
direction: rtl !important;
text-align: right !important;
font: normal 9pt Tahoma !important;
line-height: 1.8em !important;
padding: 1px 5px !important;
height: 100px !important;
}

body#profile #content h2.thumb img#profile-image, body#profile #content h2.thumb img#profile-image a { 
border: 0px solid #fff !important;
}

span.entry-content {
float: right !important;
margin: 5px 0 !important;
direction: rtl !important;
text-align: right !important;
font: normal 8pt Tahoma !important;
color: #000 !important;
}

.entry-title {
direction: rtl !important;
text-align: right !important;
font: bold 1.3em Times New Roman !important;
line-height: 1.8em !important;

}

p.entry-title {
text-align: justify !important;
}

#side div.section {
margin-bottom: 2em;

}

#side #currently {
float: right !important;
font: normal 8pt Tahoma !important;
direction: rtl !important;
text-align: right !important;
}

.info textarea {
direction: rtl !important;
text-align: right !important;
font: normal 8pt Tahoma !important;
}
}
