style.css:
#main-window[privatebrowsingmode] #navigator-toolbox:-moz-lwtheme {
  background-color: rgba(148,0,211,0.3) !important;
  }

#main-window[privatebrowsingmode] #navigator-toolbox:not(:-moz-lwtheme) {
  background-color: rgba(148,0,211,0.7) !important;
  }
  
 #main-window[sizemode="normal"][privatebrowsingmode] #titlebar-content:not(-moz-lwtheme) {
  background-color: rgba(148,0,211,0.7) !important;
  height: 23px !important;
  }
  
   #main-window[sizemode="normal"][privatebrowsingmode] #titlebar-content:-moz-lwtheme {
  background-color: rgba(148,0,211,0.3) !important;
  height: 23px !important;
  }
  
  
 #main-window[privatebrowsingmode] #TabsToolbar {
  background-color: rgba(0,0,0,0.0) !important;
  }
 
 #main-window[sizemode="normal"][privatebrowsingmode] #appmenu-button {
  margin-bottom: 2px !important;
  }
