 * Description:       Removes the gmail logo, unnecessary linke breaks, and email address from the printer friendly email message page for Gmail version 2013
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {

}

/* HEADER - LOGO */
IMG[src="/mail/help/images/logo1.gif"] { 
    display: none !important; 
  }

HTML > BODY > DIV > TABLE:first-child {
    display: none !important; 
  }
  
HTML > BODY > DIV:first-child > HR {
    display: none !important; 
  }
  
HTML > BODY > DIV:first-child > DIV > TABLE:first-child ,
HTML > BODY > DIV:first-child > DIV > HR {
    display: none !important; 
  }
  
HTML > BODY > DIV:first-child > DIV > TABLE:last-child > TBODY > TR:first-child ,
HTML > BODY > DIV:first-child > DIV > TABLE:last-child > TBODY > TR > TD > FONT:first-child {
    display: none !important; 
  }
