style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("commafeed.com"){

	/*visitation*/
	.entry-title > a.ng-binding:visited, .entry-body-content > div.ng-binding a:visited{
	    color: indigo !important;
	}

	/*transitional hightlighting*/
    .entry-title > a.ng-binding:link, .entry-body-content > div.ng-binding a:link{
        -moz-transition: background-color 6s ease-in 2s;
		-webkit-transition: background-color 6s ease-in 2s;
		-o-transition: background-color 6s ease-in 2s;
    }
    .entry-title > a.ng-binding:hover, .entry-body-content > div.ng-binding a:hover{
        background-color: lime;
        -moz-transition: background-color 1s ease-out 0.35s;
        -webkit-transition: background-color 1s ease-out 0.35s;
        -o-transition: background-color 1s ease-out 0.35s;
    }
    .entry-title > a.ng-binding:active, .entry-body-content > div.ng-binding a:active{
        background-color: lime;
        -moz-transition: none;
        -webkit-transition: none;
        -o-transition: none;
    }

    /*hovered item*/
	.entry-heading:hover{
	    box-shadow: inset 0 0 3px 1px lime;
	}

	/*highlight up/down buttons*/
    a.btn[title="Previous entry"],a.btn[title="Next entry"]{
        box-shadow: inset 0 0 2px 1px hsla(120, 80%, 50%, 1.0) !important;
    }

    /*highlight current selected tree item*/
    .css-treeview .selected{
        box-shadow: 0 0 2px 1px hsla(7,63%,52%,1);
    }

		/*junk selectors and rules*/
		/*background-color: hsla( 0, 0%, 95%, 1.0) !important;*/
}
