style.css:
/*
 * Stackoverflow / StackExchange CSS fixes
 * for Firefox in "Zoom Text Only" mode
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
               url("http://stackoverflow.com/"),
               url("http://superuser.com/"),
               url("http://serverfault.com/"),
               url("http://meta.stackoverflow.com/"),
               url("http://meta.superuser.com/"),
               url("http://meta.serverfault.com/")
{
    #question-mini-list
    
    {clear:both;}
}

@-moz-document
               url-prefix("http://stackoverflow.com/users/"),
               url-prefix("http://superuser.com/users/"),
               url-prefix("http://serverfault.com/users/"),
               url-prefix("http://meta.stackoverflow.com/users/"),
               url-prefix("http://meta.superuser.com/users/"),
               url-prefix("http://meta.serverfault.com/users/"),
               regexp("http://\w+.stackexchange.com/users/")
{
    .user-header-left,
    .user-panel > .user-panel-content > table
    
    {clear:both;}
}
