style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.last.fm"), domain("www.lastfm.ru") {

.LastAd *,
.site-footer,
.promo-unit,
.globe_icon,
.submit,
#loginLink,
#signupLink,
#lastfmLogo
    {display: none !important;}

#headerWrapper
    {margin: 0 !important;}

#fauxHeader,
#lastfmLogo,
.profile-link,
#idBadgerUser,
#idBadgerDropper,
.nav-link,
.js-search,
#content,
body
    {background-image: none !important;}

.nav-link,
#idBadgerUser
    {text-shadow: none !important;}

body
    {background-color: #333 !important;
    background-repeat: none !important;}

}
