style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("baidu.com") {
#content_left,.s_post_list,#page-main-content{width:1200px!important}
.result,.result-op,.result-item,.s_post{
  float:left!important;
  border: 1px solid rgba(0,0,0,.2)!important;
  border-radius:4px!important;
  padding:5px!important;
  margin-right:5px!important;
  margin-bottom:5px!important;
  }
[class^="ad"],[id*="_ad"],#union_mod,[id^="ecomAdDiv"],
.widget-ads.bannerdow,.home-focus,#right-ads
{display:none!important}
#content_right,.history_con.clearfix,
#page-main-slider,.mb-layout-bd.column4{display:none!important}
#lrcCol,.mb-layout-bd.column2{margin-right:-180px!important}
}
