body {                                                                                      
    background: #262729 !important;
    color: #fff !important;
}
header {
    border-bottom: 1px solid #444;
}
h1,h2,h3,h4,h5,h6 {
    color: #aaa !important;
}
 
p {
    color: #fff !important;
}
li {
    color: #acacac !important;
}
.stream_container, .stream_element .reshare, .stream_element .post-content .reshare {
    border-left: 1px solid #444 !important;
}
#publisher, .rightBar .title, .stream .stream_element {
    border-bottom: 1px #444 solid !important;
}
.show_comments {
    border-top: 1px #444 solid !important;
}
.comment.media {
    margin-left: 0 !important;
    margin-right: 0 !important;
    border: 1px solid #353639 !important;
    padding-top: 1px !important;
    background: #252629 !important;
}
.media .bd {
  padding-top:2px !important;
}
.comment.no-border.media { /* comment textarea container */
    border: none !important;
    padding: 2px !important;
    background: none !important;
}
form.new_comment textarea {
font-size: 12px !important;
width: 100% !important;
}
.stream_element .new_comment_form_wrapper {
	width: auto !important;
}
.button.creation {
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#3A3B3D), to(#414244)) !important;
    background-image: -moz-linear-gradient(top, #3A3B3D 0%, #414244 100%) !important;
    background-image: -o-linear-gradient(top, #3A3B3D 0%, #414244 100%) !important;
    color: white !important;
    border: 1px solid #555 !important;
}
#publisher_textarea_wrapper {
    box-shadow: 2px 2px 6px #444 !important;
}
.stream_element {
    border: 1px #444 solid !important;
    box-shadow: 2px 2px 6px #1A1A1A !important;
    margin-top:8px !important;
    background:#151619 !important;
}
.stream .stream_element:first-child {
  	border-top: 1px #444 solid !important;
}
.post-content {
  padding-top:4px !important;
}
ul.left_nav a.home_selector:hover, .tag:hover {
  	background: #454649 !important;
    border-radius: 3px !important;
    text-decoration:none !important;
    color:#eeeeef !important;
}
.tag {
    background: #454649 !important;
    color:#dadbdd !important;
    border-radius: 3px !important;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.45) !important;
    padding: 0px 6px 0px 6px !important;
    margin: 2px 0 2px 0 !important;
}
.tag:hover {background: #555659 !important; color: #fafafc;}

}
