/**
 * @title Twirpx NoAds
 * @description Hides all ads containers on page.
 * @version 0.1
 * @author alxzaur
 * @history
 * v0.1 - Initial release.
 */
@-moz-document domain("twirpx.com") {
    .banner,
    div[class^='banner'] {
        width: 0 !important;
        height: 0 !important;
        display: none !important;
    }
}
