/**
 * @title Twirpx FileView
 * @description Fixes large text overflow under the cover on file page.
 * @version 0.1
 * @author alxzaur
 * @history
 * v0.1
 * - Initial release.
 */
@-moz-document url-prefix("http://www.twirpx.com/file/") {
    .download .text.bb_code {
        padding-left: 260px !important;
        overflow: visible;
    }
    .download .text .pvi {
        display: block;
        float: left;
        width: 240px;
        margin: 0 0 15px -260px !important;
    }
    .download .text .pvi img {
        display: block;
        max-width: 240px !important;
        height: auto;
        margin: 0 auto;
    }
}
