style.css:
/* AGENT_SHEET */
/* Built on code by Caleb531 */

/* ::::::::::::::::::::::::::::::::::::::::::: Snow Leopard dialogues :::::::::::::::::::::::::::::::::::::: */

/* OVERLAY */
tabmodalprompt{	    background: rgba(0,0,0,.7) !important;
    				transition: all .5s ease-in-out !important;    }
/* BOX */
tabmodalprompt .mainContainer {     border-radius: 6px !important;
                                    border: none !important;
                                    background: url("chrome://global/skin/media/imagedoc-lightnoise.png"), rgba(20,20,20,.75) !important;
                                    box-shadow: inset 0 2px 3px 1px #000 !important;
                                    color: #fff !important;	    }
/* BOTTOM CONTAINER */
tabmodalprompt .buttonContainer{		background: none !important;    }

/* TEXT BOX, TOP CONTAINER */
tabmodalprompt .topContainer textbox{	height: 24px !important;        }

/* ::::::::::::::::::::::::::::::::::::::::::: DUST tooltips :::::::::::::::::::::::::::::::::::::: */

tooltip {   -moz-appearance: none !important;
            border-radius: 6px !important;
            background: url("chrome://global/skin/media/imagedoc-lightnoise.png"), rgba(20,20,20,.75) !important;
            box-shadow: inset 0 2px 3px 1px #000 !important;
            -moz-window-shadow: none !important;    }

tooltip label { margin: 0px !important;
                padding: 1px 3px !important;
                text-shadow: #000 0 0 1px, #000 0 0 3px;
                color: #fff !important; }
/* ::::::::::::::::::::::::::::::::::::::: DUST menus ::::::::::::::::::::::::::::::::::::::: */

/* SUBMENUS */
popup menu, menupopup menu,
popup:not(#autoscroller), menupopup{ -moz-appearance: none !important; background: none !important;
                                     border: none !important;   color: #fff !important; }
                                     
popup scrollbox, menupopup scrollbox {   margin: 5px !important;    }

/* MENU ITEMS */
menuitem, .splitmenu-menuitem { -moz-appearance: none !important;   border: none !important;
                                height: 18px !important; color: #fff !important; 
                                text-shadow: #000 0 0 1px, #000 0 0 3px; }
/* APP MENU */
#appmenu-popup vbox {   background: none !important;
                        border: none !important;
                        box-shadow: none !important;    }
/* MENUS */

menupopup, popup:not(#autoscroller){    border-radius: 6px !important;
                                        background: url("chrome://global/skin/media/imagedoc-lightnoise.png"), rgba(20,20,20,.75) !important;
                                        box-shadow: inset 0 2px 3px 1px #000 !important;
                                        -moz-window-shadow: none !important;    }

menupopup menupopup {   margin-top: -13px !important;   }

/* SEPARATOR */
menuseparator { -moz-appearance: none !important;   list-style-image: none !important;
                background: none !important;        border: none !important;
                margin: -4px 15px 2px !important;
                border-bottom: solid .5px rgba(200,200,200,.4) !important; 
                box-shadow: inset 0 -1px 0 #111 !important; }
/* DISABLED ITEM */
menuitem[disabled="true"] { background: none !important;
                            opacity: 0.25 !important;   }
/* SELECTED ITEM */
popup menu:not([disabled="true"])[_moz-menuactive="true"], menupopup menu:not([disabled="true"])[_moz-menuactive="true"],
menuitem:not([disabled="true"])[_moz-menuactive="true"], .splitmenu-menuitem:not([disabled="true"])[_moz-menuactive="true"] {
                        background: -moz-radial-gradient(50% 0%, RGBa(/*[[c]]*/, .8), transparent 100%) !important; 
                        border: none !important; font-weight: bold !important; margin: 0 -4px !important; 
                        border-top: 1px solid RGBa(/*[[c]]*/, .8) !important; border-radius: 0 0 4px 4px !important; 
                        text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px !important;   }

/* SELECTED ITEM - PRIVATE BROWSING */			
#main-window[privatebrowsingmode] popup menu:not([disabled="true"])[_moz-menuactive="true"], 
#main-window[privatebrowsingmode] menupopup menu:not([disabled="true"])[_moz-menuactive="true"],
#main-window[privatebrowsingmode] menuitem:not([disabled="true"])[_moz-menuactive="true"], 
#main-window[privatebrowsingmode] .splitmenu-menuitem:not([disabled="true"])[_moz-menuactive="true"] {
                        background: -moz-radial-gradient(50% 0%, RGBa(/*[[pc]]*/, .8), transparent 100%) !important; 
                        border-top: 1px solid RGBa(/*[[pc]]*/, .8) !important; }

/* RIGHT ARROW */
.menu-right, autorepeatbutton{  -moz-appearance: none !important;
                                opacity: .4 !important; margin-top: 1px !important; 
                                list-style-image: url("data:image/png;base64,") !important;
                                -moz-image-region: rect(0px 7px 9px 0px) !important;    }
                                
.menu-right[_moz-menuactive="true"] {   opacity: 1 !important;  }

/* SCROLL ARROWS */
.autorepeatbutton-up, .autorepeatbutton-down {  position: relative !important;
                                                z-index: 1 !important;
                                                opacity: 0.75 !important;
                                                background: #333 !important; }
.autorepeatbutton-up[disabled="true"],
.autorepeatbutton-down[disabled="true"] {   opacity: 0.4 !important;    }

/* CHECKMARK */
menuitem[checked="true"] .menu-iconic-left{ -moz-appearance: none !important;
                                            background: url("data:image/png;base64,") -2px -1px no-repeat !important;
                                            opacity: 0.5 !important;    height: 16px !important;    list-style-image: none !important;  }           
                                            
menuitem[checked="true"][_moz-menuactive="true"] .menu-iconic-left {    opacity: 1.0 !important;    }

/* ::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::: DUST Awesome Bar ::::::::::::::::::::::::::::::::::::::::::::::::: */

/* GENERAL */
.autocomplete-richlistbox { -moz-appearance: none !important;
                            margin: 6px 2px !important;  
                            background: none !important;
                            color: #fff !important;     }
                            
.autocomplete-richlistitem {    color: #fff !important;  }

panel[type="autocomplete-richlistbox"]{ -moz-appearance: none !important;
                                        border-radius: 6px !important;
                                        background: url("chrome://global/skin/media/imagedoc-lightnoise.png"), rgba(20,20,20,.75) !important;
                                        box-shadow: inset 0 2px 3px 1px #000 !important;
                                        -moz-window-shadow: none !important; 
                                        border: none !important;
                                        margin: 2px 0 0 !important; }
                                        
panel[type="autocomplete-richlistbox"]
.autocomplete-richlistbox richlistitem:not([selected="true"]):nth-child(odd),
.autocomplete-richlistbox richlistitem:not([selected="true"]):nth-child(even) { background-color: transparent !important;   }

/* TITLE TEXT */
.ac-comment {   color: #fff !important; }
/* URL TEXT */
.ac-url-text:not([selected="true"]),
.ac-action-text:not([selected="true"]) {    color: #999 !important; }
/* SWITCH TO TAB */
.autocomplete-richlistbox richlistitem[actiontype="switchtab"] > .ac-url-box > .ac-action-icon { opacity: .5 !important;
                                                                                                 list-style-image: url("data:image/png;base64,") !important; }
/* SWITCH TO TAB */
richlistitem[actiontype="switchtab"][selected="true"] > .ac-url-box > .ac-action-icon { opacity: 1 !important;    }
/* SEPARATOR */
.autocomplete-richlistitem {    border-top: solid 0px !important;
                                border-bottom: solid .5px rgba(200,200,200,.4) !important; 
                                box-shadow: inset 0 -1px 0 #111 !important;
                                margin: 0 10px 0 !important; }
/* SELECTED ITEM */
.autocomplete-richlistitem[selected="true"] {   background: -moz-radial-gradient(50% 0%, RGBa(/*[[c]]*/, .8), transparent 100%) !important; 
                                                border: none !important; font-weight: bold !important; margin: 0 -4px !important; 
                                                border-top: 1px solid RGBa(/*[[c]]*/, .8) !important; border-radius: 0 0 4px 4px !important; 
                                                text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px !important;   
                                                box-shadow: none !important;    }

/* SELECTED ITEM - PRIVATE BROWSING */	
#main-window[privatebrowsingmode] 
.autocomplete-richlistitem[selected="true"] {   background: -moz-radial-gradient(50% 0%, RGBa(/*[[pc]]*/, .8), transparent 100%) !important; 
                                                border-top: 1px solid RGBa(/*[[pc]]*/, .8) !important; border-radius: 0 0 4px 4px !important; }


                                                
.ac-url-text[selected="true"] { color: #fff !important; }

/* HIDE SCROLLBARS
.autocomplete-richlistbox scrollbar[orient="vertical"] {    opacity: 0.0 !important;
                                                            margin-right: -15px !important; }

/* BOOKMARKED RESULTS ICON */
richlistitem .ac-result-type-bookmark { list-style-image: url("data:image/png;base64,") !important;
                                        -moz-image-region: rect(0px 16px 16px 0px) !important;
                                        opacity: 0.4 !important;    }
/* BOOKMARKED RESULTS ICON (SELECTED) */
richlistitem[selected="true"] .ac-result-type-bookmark {    opacity: 1.0 !important;    }

/* TAGGED RESULTS ICON */
richlistitem .ac-result-type-tag {  list-style-image: url("data:image/png;base64,") !important;
                                    opacity: 0.4 !important; }
/* TAGGED RESULTS ICON (SELECTED) */
richlistitem[selected="true"] .ac-result-type-tag { opacity: 1.0 !important;    }

/* ::::::::::::::::::::::::::::::::::::::::::: Snow Leopard bookmark panel :::::::::::::::::::::::::::::::::::::: */

/* GENERAL */
#editBookmarkPanel {    -moz-appearance: none !important;
                        border-image-source: url("data:image/png;base64,") !important;
border-image-slice: 26 50 22 20 fill !important;
border-image-width: 26px 50px 22px 20px !important;
border-image-repeat: repeat !important;
-moz-window-shadow: none !important;
margin-right: -36px !important;
opacity: 1.0 !important;
}
#editBookmarkPanelContent {
width: 300px !important;
}
/* REMOVE PANEL OVERLAY */
#editBookmarkPanel > box > box,
#editBookmarkPanel > box > box > box {
border: none !important;
background: none !important;
color: #fff !important;
box-shadow: none !important;
}
#editBookmarkPanel > box > box {
margin: 5px 5px 5px !important;
}
#editBookmarkPanel > box > box > image {
display: none !important;
}

/* INPUT FIELDS */
#editBMPanel_tagsField,
#editBMPanel_namePicker[droppable="false"] > .menulist-editable-box {
background: #fff !important;
color: #000 !important;
box-shadow: none !important;
}

/* BIG STAR ICON */
#editBookmarkPanelStarIcon {
list-style-image: url("data:image/png;base64,")
!important;
opacity: 0.4 !important;
}

/* BUTTONS */
#editBookmarkPanelBottomButtons > button,
#editBookmarkPanelRemoveButton {
-moz-appearance: button !important;
background: none !important;
border: none !important;
color: #000 !important;
font-size: 8pt !important;
min-width: 76px !important;
min-height: 19px !important;
text-shadow: none !important;
box-shadow: none !important;
}

/* FOLDER MENULIST */
#editBMPanel_folderMenuList {
-moz-appearance: menulist !important;
padding: 0px !important;
background: none !important;
border: none !important;
box-shadow: none !important;
color: #000 !important;
text-shadow: none !important;
}
.menulist-dropmarker {
display: none !important;
}

/* FOLDER TREE */
#editBookmarkPanel #editBMPanel_folderTree,
#editBookmarkPanel #editBMPanel_newFolderBox,
#editBookmarkPanel #editBMPanel_tagsSelector {
margin-left: -55px !important;
margin-right: 0px !important;
border: solid 1px #777 !important;
border-radius: 0px !important;
background: #333 !important;
color: #fff !important;
}
#editBookmarkPanel #editBMPanel_folderTree > stack,
#editBookmarkPanel #editBMPanel_tagsSelector > listrows {
background: #333 !important;
}
#editBookmarkPanel #editBMPanel_newFolderBox {
background: none !important;
border: none !important;
}
#editBookmarkPanel #editBMPanel_newFolderButton {
border: none !important;
background: none !important;
list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAYAAADED76LAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAHolAACAgwAA+f8AAIDpAAB1MAAA6mAAADqYAAAXb5JfxUYAAAAsSURBVHjaYvz//z8DEoBxGGECTAwEAEEFLEjGYrOKOBMYaetIAAAAAP//AwCSsgcQQxrbgAAAAABJRU5ErkJggg==")
!important;
margin-top: -1px !important;
opacity: 0.5 !important;
}
#editBookmarkPanel #editBMPanel_newFolderButton:hover:active {
opacity: 0.4 !important;
}
/* DISCLOSURE TRIANGLES */
#editBMPanel_folderTree treechildren::-moz-tree-twisty {
-moz-appearance: none !important;
list-style-image: url("data:image/png;base64,")
!important;
-moz-image-region: rect(0px 7px 9px 0px) !important;
width: 7px !important;
}
#editBMPanel_folderTree treechildren::-moz-tree-twisty(open) {
-moz-image-region: rect(0px 25px 9px 16px) !important;
width: 9px !important;
margin-left: -2px !important;
}
/* TREE ROW */
#editBookmarkPanel
#editBMPanel_folderTree treechildren::-moz-tree-cell-text,
#editBookmarkPanel:-moz-system-metric(mac-graphite-theme)
#editBMPanel_folderTree treechildren::-moz-tree-cell-text {
color: #fff !important;
}
/* SELECTED ROW */
#editBookmarkPanel
#editBMPanel_folderTree treechildren::-moz-tree-row(selected) {
background: -moz-radial-gradient(50% 0%, RGBa(/*[[c]]*/, .8), transparent 100%) !important; 
                        border: none !important; font-weight: bold !important; margin: 0 -4px !important; 
                        border-top: 1px solid RGBa(/*[[c]]*/, .8) !important; border-radius: 0 0 4px 4px !important; 
                        text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px !important; 
}


/* EXPANDERS */
#editBookmarkPanel #editBMPanel_foldersExpander,
#editBookmarkPanel #editBMPanel_tagsSelectorExpander {
-moz-appearance: none !important;
background: none !important;
border: none !important;
outline: none !important;
list-style-image: url("data:image/png;base64,")
!important;
box-shadow: none !important;
}
#editBookmarkPanel .expander-up {
-moz-image-region: rect(0px 18px 18px 0px) !important;
}
#editBookmarkPanel .expander-up:hover:active {
opacity: 0.75 !important;
}
#editBookmarkPanel .expander-down {
-moz-image-region: rect(0px 36px 18px 18px) !important;
}
#editBookmarkPanel .expander-down:hover:active {
opacity: 0.75 !important;
}

/* HIDE SCROLLBARS */
#editBookmarkPanel #editBMPanel_folderTree scrollbar[orient="vertical"] {
opacity: 0.0 !important;
margin-left: -15px !important;
}

/* ::::::::::::::::::::::::::::::::::::::::::: Snow Leopard identity panel :::::::::::::::::::::::::::::::::::::: */

/* GENERAL */
#identity-popup {
border-image-source: url("data:image/png;base64,") !important;
border-image-slice: 26 21 22 50 fill !important;
border-image-width: 26px 21px 22px 50px !important;
border-image-repeat: repeat !important;
margin-left: -36px !important;
padding: 20px !important;
padding-left: 44px !important;
}

#identity-popup > box > box,
#identity-popup > box > box > box {
background: none !important;
border: none !important;
color: #fff !important;
box-shadow: none !important;
}
#identity-popup > box > box {
margin: 5px -10px -15px -40px !important;
}
#identity-popup > box > box > image {
display: none !important;
}
