@namespace url(http://www.w3.org/1999/xhtml);
/* Highlight Unvisited Links */
/* http://userstyles.org/styles/89450 */
/* Created by colt365 */
/* version 1 */


@-moz-document
regexp('.*'){
    a:link {
        border:1px solid red !important;
    }
    a:visited {
        border:1px solid white !important;
    }
}
