style.css:
@-moz-document domain("valuewalk.com") {
#title, #description {display: none}
/*#header,*/ /*--.main-navigation {background: #272727 right no-repeat url('http://cdnet.valuewalk.com/wp-content/uploads/2013/03/banner-logoresized.jpg') !important;}--*/

/* no social */
.mr_social_sharing_wrapper,
.dc-social-slick,
#text-94.side-widget.widget_text {display:none}

/* no comment */
#fbComments {display: none !important}

/* internal advertising */
#hellobar-pusher, #hellobar-wrapper, /*Subscribe (banner)*/
#mc_embed_signup, /*Subscribe (sidebar)*/
.storycontent P:last-of-type > I:first-child,
.storycontent P:last-of-type > A:last-child, /*Subscribe (end of article)*/
#text-77.side-widget.widget_text /*send tips*/ {display: none !important}

/* external advertising */
.header-widget,
#text-113.header-widget.widget_text,
#text-119.side-widget.widget_text,
#text-120.side-widget.widget_text,
#text-113.side-widget.widget_text,
[id*="adsense"],
IFRAME[src*="doubleclick"] {display:none}

/* syndicated "content" links */
[id^="taboola"], /*by Taboola*/
#text-129.side-widget.widget_text, [id^="ac_"], /*ac_adbox*/
#text-127.side-widget.widget_text, /*Ads by Yahoo!*/
#text-116.side-widget.widget_text /*Newsmax Headlines*/
/*#lwl-widget-text-129*/ {display: none !important}
}
