style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("frontpagemag.com") {

.subWrapper, .mainadRight, .mainadLeft, #logo,
 #sidebar-alt, #sidebar, #paginate-slider2, .sliderwrapper,
  #footerPages, #disqus_thread, .sub-title {
    display: none !important;
    }

.content-sidebar-sidebar #inner, .content-sidebar #inner {
    background: #fff !important;
    }
#inner {
    border: none !important;
    }

.content-sidebar-sidebar #content, 
#enhancedtextwidget-10 > div:nth-child(1) > div:nth-child(1) > a:nth-child(2) > img:nth-child(1)  {
    width: 940px !important;
}

.content-sidebar #content {
    width: 700px !important;
    }
ul#columnistlist li {
    width: 210px !important;
    }
p {
    font-size: 1.5em !important;
    line-height: 1.3 !important;
    color: #717171 !important;
    }

#columnists {
    font-size: 1.2em !important;
    }

h2 a, ul#columnistlist li a {
    color:#011bcb !important;
    }

h2 a:hover, ul#columnistlist li a:hover {
    color: #cb0400 !important;
    }
}
