style.css:
@-moz-document domain("www.inoreader.com") {
#subscriptions_buttons { background-color: #efefef !important; }
#tree_pane             { background-color: #efefef !important; width: 275px !important; }
.article_card          { background-color: #efefef !important;
                         margin: 10px !important;
                         padding: 5px !important; }
#reader_pane           { background-color: #CCCCF5 !important; }
.unread_cnt            { color: red !important; }

.no_unread             { border: 5px ridge grey !important;
                         background-color: #fff !important;
                         background-image:url('http://www.csuchico.edu/hr/images/alert2.png') !important;
                         background-repeat:no-repeat !important;
                         width: 500px !important;
                         padding-left: 130px !important; }
#no_more_div           { line-height: 450px !important; }
.no_more_articles      { border: 5px ridge grey !important;
                         padding: 15px !important;
                         background-color: #fff !important;
                         font-weight: bold !important;
                         font-size: 16pt !important;
                         color: red !important;
                         -moz-border-radius: 25px !important;
                         border-radius: 25px !important; }
.article_content       { font-size: 12pt !important; }
P,LI                   { font-size: 12pt !important; }
H1                     { font-size: 18pt !important; }
H2                     { font-size: 16pt !important; }
H3                     { font-size: 14pt !important; }
H4                     { font-size: 12pt !important; }
H5                     { font-size: 10pt !important; }
H6                     { font-size: 8pt  !important; }
}
