style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theverge.com") {
#container > DIV > ARTICLE > ASIDE > SECTION:last-child > DIV {
    display: none!important;
}

#container > DIV > ARTICLE > ASIDE > SECTION:last-child > P {
    display: none!important;
}

#container > DIV > ARTICLE > ASIDE > SECTION:last-child > UL {
    display: none!important;
}

#container > DIV > ARTICLE > ASIDE > SECTION:nth-child(2) > DIV {
    display: none!important;
}

#container > DIV > ARTICLE > ASIDE > SECTION:nth-child(2) > UL {
    display: none!important;
}

#container > DIV > ARTICLE > ASIDE > SECTION:nth-child(3) > H2 {
    display: none!important;
}

#container > DIV > ARTICLE > ASIDE > SECTION:nth-child(3) > UL {
    display: none!important;
}

#container-content > ARTICLE > DIV > DIV:nth-child(2) > DIV, social-media-column, clearfix, instapaper_ignore, entry-unrelated {
    display: none!important;
}

.social-col{
    display: none!important;
}
    
.rightcol{
    display: none!important;
}

.story-navigation {
    display: none!important;
}

.column{
    width: auto!important;
}

.article-body {
    width: auto!important;
}

p.pgh-paragraph {
    font-family: Helvetica!important;
    font-size: 14pt!important;
    line-height: 1.25em!important;
}


.breaking-news{
    width: auto!important;
}


}
