style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("feedly.com") {
/*以下是左侧导航栏里面的东东*/
#feedlyTabsUnpin,
#feedlyTabsPin,
#mytab_label,
#savedtab_label,
#discovertab_label,
#latesttab_label,
[data-uri="label/feedly.history"],
.label[data-uri="contents/edit"],
[data-uri="themes"],
[data-uri="preferences"],
[data-uri="pro/support"],
.label.primary.iconless[href^="https://plus.google.com/communities/"],
.label.primary.iconless[href^="http://feedly.com/apps.html"],
[data-app-action="askLogout"],
[data-uri="pro/navbar"],
[data-uri="privacy"],
[data-uri="privacy"],
[data-uri="terms"],
 /*以下是齿轮里面的东东*/
.popup.customizer>.section:first-child>.capitalize,
.popup.customizer>.section:nth-child(2)>.capitalize,
.popup.customizer>.section:nth-child(3)>.capitalize,
[data-page-action="markAsRead"],
[data-page-action="markAsReadOneDay"],
[data-page-action="markAsReadOneWeek"],
[data-page-action="toggleUnreadOnlyFilter"],
[data-page-action="toggleOldestFirstKnob"],
#lvc_u0,
#lvc_u4,
#lvc_u5,
#lvc_u100,
#lvc_category,
[data-page-action="toggleCurrentSubscriptionOpenInWebsiteDirectly"],
[data-page-action="editCurrentSubscription"],
[data-page-action="unsubscribeFromCurrentSubscription"],
/*以下是正文里的东西*/
[data-page-entry-action="keepEntryAsUnread"],
.action[title="mark as read and remove from list"],
[data-page-entry-action="previewEntry"],
.websiteCallForAction,
.previewHeader-links span:last-child,
[data-page-action="askDeleteTag"],
[data-app-action-input="keyboard navigation"],
#bigMarkAllAsReadButton,
[data-page-action="askClearSaved"]
  {
    font-size: 0!important;
}
/*以下是左侧导航栏里面的东东*/
#feedlyTabsUnpin:before,
#feedlyTabsPin:before,
#mytab_label:before,
#savedtab_label:before,
#discovertab_label:before,
#latesttab_label:before,
[data-uri="label/feedly.history"]:before,
.label[data-uri="contents/edit"]:before,
[data-uri="themes"]:before,
[data-uri="preferences"]:before,
[data-uri="pro/support"]:before,
.label.primary.iconless[href^="https://plus.google.com/communities/"]:before,
.label.primary.iconless[href^="http://feedly.com/apps.html"]:before,
[data-app-action="askLogout"]:before,
[data-uri="pro/navbar"]:before,
[data-uri="privacy"]:before,
[data-uri="terms"]:before,
/*以下是齿轮里面的东东*/
.popup.customizer>.section:first-child>.capitalize:before,
.popup.customizer>.section:nth-child(2)>.capitalize:before,
.popup.customizer>.section:nth-child(3)>.capitalize:before,
[data-page-action="markAsRead"]:before,
[data-page-action="markAsReadOneDay"]:before,
[data-page-action="markAsReadOneWeek"]:before,
[data-page-action="toggleUnreadOnlyFilter"]:after,
[data-page-action="toggleOldestFirstKnob"]:after,
#lvc_u0:after,
#lvc_u4:after,
#lvc_u5:after,
#lvc_u100:after,
#lvc_category:after,
.websiteCallForAction:before
  {
  font-size: 14px;
}
  
/*小字体*/  
[data-page-action="toggleCurrentSubscriptionOpenInWebsiteDirectly"]:before,
[data-page-action="editCurrentSubscription"]:before,
[data-page-action="unsubscribeFromCurrentSubscription"]:before,
[data-page-entry-action="keepEntryAsUnread"]:before,
.action[title="mark as read and remove from list"]:before,
[data-page-entry-action="previewEntry"]:before,
.previewHeader-links span:last-child:before,
[data-page-action="askDeleteTag"]:before,
[data-app-action-input="keyboard navigation"]:before,
[data-page-action="askClearSaved"]:before
  {
    font-size: 12px;
}
#bigMarkAllAsReadButton:before
  {
    font-size: 16px;
}
/*以下是左侧导航栏里面的东东*/
#feedlyTabsUnpin:before{content:'浮动'} 
#feedlyTabsPin:before{content:'固定'} 
.pinButton{padding:8px 10px 4px 10px}
#mytab_label:before{content:'首页'} 
#savedtab_label:before{content:'稍后阅读'} 
#discovertab_label:before{content:'添加订阅'}
#latesttab_label:before{content:'全部'}
[data-uri="label/feedly.history"]:before{content:'阅读记录'}
.label[data-uri="contents/edit"]:before{content:'组织'}
[data-uri="themes"]:before{content:'主题'}
[data-uri="preferences"]:before{content:'选项'}
[data-uri="pro/support"]:before{content:'专业版'}
.label.primary.iconless[href^="https://plus.google.com/communities/"]:before{content:'蓝图'}
.label.primary.iconless[href="http://feedly.com/apps.html"]:before{content:'移动应用'}
[data-uri="privacy"]:before{content:'隐私政策'}
[data-uri="terms"]:before{content:'服务条款'}
[data-app-action="askLogout"]:before{content:'退出登陆';font-size: 12px}
[data-uri="pro/navbar"]:before{content:'升级'}
 /*以下是齿轮里面的东东*/
.popup.customizer>.section:first-child>.capitalize:before{content:"浏览模式"}
.popup.customizer>.section:nth-child(2)>.capitalize:before{content:"标记为已读"}
[data-page-action="markAsRead"]:before{content:"全部"}
[data-page-action="markAsReadOneDay"]:before{content:"一天前"}
[data-page-action="markAsReadOneWeek"]:before{content:"一周前"}
.popup.customizer>.section:nth-child(3)>.label.capitalize:before{content:"过滤"}
[data-page-action="toggleUnreadOnlyFilter"]:after{content:"只显示未读条目"}
[data-page-action="toggleOldestFirstKnob"]:after{content:"旧条目排在前面"}
#lvc_u0:after{content:"仅标题"}
#lvc_u4:after{content:"图文并排"}
#lvc_u5:after{content:"卡片"}
#lvc_u100:after{content:"全文"}
#lvc_category:after{content:"按源分组"}
[data-page-action="toggleCurrentSubscriptionOpenInWebsiteDirectly"]:before{content:"直接在网页中打开"}
[data-page-action="editCurrentSubscription"]:before{content:"编辑该订阅源"}
[data-page-action="unsubscribeFromCurrentSubscription"]:before{content:"取消订阅"}
/*以下是正文里的东西*/
[data-page-entry-action="keepEntryAsUnread"]:before{content:"保持未读"}
.action[title="mark as read and remove from list"]:before{content:"隐藏"}
[data-page-entry-action="previewEntry"]:before{content:"预览"}
.websiteCallForAction:before{content:"查看原网页"}
.previewHeader-links span:last-child:before{content:"在新标签中打开";}
.previewHeader-links span:last-child{margin-right:12px;}
#bigMarkAllAsReadButton:before{content:"全部标记为已读"}
  
[data-page-action="askClearSaved"]:before{content:"全部清除"}
 [data-page-action="askClearSaved"]{padding:17px 17px 8px;} 
[data-page-action="askDeleteTag"]:before{content:"删除"}
[data-page-action="askDeleteTag"]{padding:16px 17px 8px 17px;}
[data-app-action-input="keyboard navigation"]:before{content:"键盘快捷键"}
}
