/*
 * Google Web Search - Dark Blue Theme
 *
 * Author      : gilles
 * Updated     : July 24th, 2008
 * Description : Modifies the colors on Le Monde (http://www.lemonde.fr/)
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 * Thanks to   : Valacar for its wonderful Google Web Search - dark blue redesign
 */

@-moz-document domain("www.lemonde.fr") {

  body {
    background: #253960 !important;
    color: #fff !important;
  }

  /* defaults for all links */
  a:link, a:visited, .link { color: #82BDFF !important; }
  a:active, .link:active { color: #92CDFF !important; }

  div,td {
    background-color: #253960 !important;
    color: #fff !important;
  }

}
