style.css:
@-moz-document domain(flickr.com)
{
#eyebrow {background:rgba(0,0,0,0.8)!important;color:pink !important;}
#yucs-top-bar a {font-size:240% !important; font-weight:bold !important; text-decoration:blink !important;}
#yucs-top-bar  li {margin: 2px 15px 0 0!important;}
#yucs-top-bar ul li a, #yucs-top-list ul li a  {background:rgba(0,0,0,0.8)!important;color:pink !important;}
#yucs-top-list {background:rgba(0,0,0,0.8)!important;color:pink !important;}

.yucs-top-ico {xxxmargin-right:-70px!important;}
#global-nav {top:70px !important;}
#eyebrow {height: 50px!important;padding-top:20px!important}
body.with-eyebrow {margin-top:50px!important;}
body.with-eyebrow {background-position: 0 0 !important;}

#yucs-top-bar {
  -webkit-animation: blink 1s steps(5, start) infinite;
  -moz-animation:    blink 1s steps(5, start) infinite;
  -o-animation:      blink 1s steps(5, start) infinite; 
  animation:         blink 1s steps(5, start) infinite;
}

@-webkit-keyframes blink {
  to { visibility: hidden; }
}
@-moz-keyframes blink {
  to { visibility: hidden; }
}
@-o-keyframes blink {
  to { visibility: hidden; }
}
@keyframes blink {
  to { visibility: hidden; }
}
}
