style.css:
@-moz-document domain("plus.google.com") {
#content div[guidedhelpid="sharebox_launcher"] span[role=button] > div:last-child {
  display: none !important;
}

#content div[guidedhelpid="sharebox_textarea"] {
  display: none !important;
}

#content div[guidedhelpid="sharebox_launcher"] > div > div:first-child > div {
  border-top: 0 !important;
}

#content div[guidedhelpid="sharebox_launcher"] > div > div:first-child > div > div > div {
  display: none !important;
}

#content div[guidedhelpid="sharebox_launcher"] > div > div:first-child {
  height: 25px !important;
}

#content div[guidedhelpid="sharebox_launcher"] {
  height: 65px !important;
}
}
