style.css:
.tabs-newtab-button {
  width: 26px !important;
  margin-top: 3px !important;
  margin-bottom: 3px !important;
  margin-left: 1px !important;
  margin-right: 1px !important;
  background-color: #A0A0A0 !important;
  border: 1px solid #A0A0A0 !important;
  border-radius: 0 !important;
}

.tabs-newtab-button:hover {
  background-color: #A0A0A0 !important;
}

.tabbrowser-tab:not([pinned]) 
{	
    margin-left: 0px !important;
    margin-top: -1px !important;
    margin-right: 1px !important;	
    border: none !important;
    border-bottom: none !important;
    height: 26px !important;
    padding-left: 1px !important;
    padding-right: 1px !important;

}

.tabbrowser-tab[pinned]
{	
    margin-top: -1px !important;
    margin-left: 0px !important;
    margin-right: 1px !important;
    border: 0px !important; 
    min-height: 19px !important;
    padding-left: 0px !important;
    padding-right: 0px !important;
}

.tabbrowser-tab[selected]
{
    background-color: #A0A0A0 !important;
      background-image: -moz-linear-gradient(rgba(255,255,255,1), rgba(255,255,255,1) 100%) !important; 

    border: 1px solid #A0A0A0 !important;
    border-bottom: none !important; 
    border-radius: 0 !important;
    border-style: none !important;
    padding-left: 2px !important;

}
    
.tabbrowser-tab:not([selected])
{
    background-color: #A0A0A0 !important;
    border: 1px solid #A0A0A0 !important;
    border-radius: 0 !important;
      background-image: -moz-linear-gradient(rgba(225,225,225,1), rgba(225,225,225,1),rgba(225,225,225,1), rgba(225,225,225,1), rgba(225,225,225,0.1)) !important; 
}
.tabbrowser-tab:not([selected]):hover{
    background-color: #A0A0A0 !important;
    border: 1px solid #A0A0A0 !important;
    border-bottom: 1px solid #908C8B !important;
    border-radius: 0 !important;
}

.tabbrowser-tab {
 padding: 0 !important;
 
}

window[sizemode="maximized"] #TabsToolbar {
 height: 24px !important;
 margin-top: 1px !important;
}

  #nav-bar { 
 background: #FFFFFF!important; }
