style.css:
@-moz-document url-prefix("http://digg.com/reader") {
html, body {
	background: #333 !important;
	color: #ccc !important;
}
.feeditem-list.expanded .story-detail-view-container,
.story-detail-view,
.site-header,
.site-header-container,
.reader-nav .reader-nav-item, 
.view-add {
	background-color: #252525 !important;
}
.col-left {
	background-color: #222 !important;
}
.reader-nav .reader-nav-item {
	border-left: 1px solid #333 !important;
}
.site-header-logo-container a {
  background: transparent !important;
  color: #aaa !important;
  text-decoration: none !important;
  text-indent: 0 !important;
  font: 24px/1.5em arial;
}
.dr-feed-selected>.dr-label, .dr-feed-selected>.dr-label:hover {
	background-color: #333 !important;
  border-top-color: #444 !important;
  border-bottom-color: #444 !important;
}
.dr-label:hover {
	background-color: #1d1d1d !important;
  border-top-color: #222 !important;
  border-bottom-color: #222 !important;
}
.dr-label {
	color: #aaa !important;
  text-shadow: 0 1px #111 !important;
}
.feeditem-list.expanded .story-detail-view-container .story-title a,
.feeditem-read .feeditem-headline {
	color: #ddd !important;
}
.feeditem-list .feeditem-feedtitle {
	font-weight: bold !important;
}
.feeditem-list {
  border-top: 1px solid #222 !important;
}
.feeditem-list.expanded,
.feed-stream-header {
  border-bottom: 1px solid #444 !important;
}
.col-left {
	border-right: 1px solid #444 !important;
}
.feeditem-list .feeditem-fuzzytime a {
	color: #aaa !important;
}
.feed-item-focused .feeditem-feedtitle, article.expanded .feeditem-feedtitle, .feed-item-focused .feeditem-list-content, article.expanded .feeditem-list-content,
.feeditem-list:hover {
	background-color: #444;
}
.view-add {
	border-top: 1px solid #444 !important;
}
.view-add>div {
  -webkit-box-shadow: inset 0 1px 0 0 #222 !important;
  box-shadow: inset 0 1px 0 0 #222 !important;
}
.dr-add-btn {
  border-right: 1px solid #444 !important;
}
}
