/* Pasek przewijania w sygnaturze */
div.signature {
	max-height: 200px;
	overflow: auto;
}

/* Ukrycie sygnatur */
div.signature {
	display: none;
}

/* Ukrycie pola "Także otagowane jako" */
div.clear.clearfix.left + br + br + h3.maintitle, div.clear.clearfix.left + br + br + h3.maintitle + div.border, br + br + div.ipsBox > h3.maintitle, br + br + div.ipsBox > h3.maintitle + div.ipsBox_container {
	display: none;
}

/* Ukrycie pola "Ostatnie wpisy w tym temacie" */
br.clear + br + h2.maintitle, br.clear + br + h2.maintitle + div.border, br.clear + br + h2.maintitle + div.ipsBox {
	display: none;
}

/* Ukrycie avatara w Szybkiej Odpowiedzi */
#fast_reply_wrapper > div.ipsBox_container > a {
	display: none !important;
}
#fast_reply_wrapper > div.ipsBox_container > div.ipsBox_withphoto {
	margin-left: 0 !important;
}

/* Zmniejszenie niektórych nagłówków */
.post_wrap h3 {
	height: 34px !important;
	line-height: 34px !important;
}
ul.post_controls a {
	height: 24px !important;
	line-height: 24px !important;
}
div.maintitle, h3.maintitle {
	height: 7px !important;
	line-height: 7px !important;
}

/* Ukrycie reputacji */
.reputation, div.rep_bar > ul.ipsList_inline, p.rep_highlight {
	display: none !important;
}

/* Zmiana domyślnego koloru postów */
.post {
	color: /*[[domyslnyKolorPostow]]*/;
}

/* Zmiana czcionki i jej koloru w Szybkiej Odpowiedzi */
.cke_skin_ips textarea.cke_source { 
	font-family: "Consolas", monospace !important;
	color: /*[[kolorSzybkaOdp]]*/;
	font-size: 12px;
}

/* Zmiana czcionki w Szybkiej Odpowiedzi */
.cke_skin_ips textarea.cke_source { 
	font-family: "Consolas", monospace !important;
	font-size: 12px;
}

/* Zmiana wyglądu dopisków "[post] Edytowany przez..." - ciemna skórka */
p.edit {
	padding-top: 3px;
	background: url("http://forum.cdaction.pl/public/style_images/infinite_dark/comment_edit.png") 6px 4px no-repeat !important;
	border: 0px;
	border-top: 1px solid #666666 !important;
	color: #FFFFFF !important;
	text-shadow: #000000 0px 1px 0px !important;
	width: 350px;
}

/* Zmiana wyglądu dopisków "[post] Edytowany przez..." - jasna skórka */
p.edit {
	padding-top: 3px;
	background: url("http://forum.cdaction.pl/public/style_images/infinite_dark/comment_edit.png") 6px 4px no-repeat !important;
	border: 0px;
	border-top: 1px solid #666666 !important;
	color: #000000 !important;
	text-shadow: #FFFFFF 0px 1px 0px !important;
	width: 350px;
}

/* Ukrycie opisów działów */
.desc.forum_desc {
	display: none;
}

/* Ukrycie przycisków społecznościowych */
h3.maintitle + div.border + script + br, h3.maintitle + div.border + script + br + div.clear.clearfix.left, h3.maintitle + div.border + script + br + div.clear.clearfix.left + br {
	display: none !important;
}

/* Ukrycie pasku "PRZEJDŹ DO INTERIA.PL" */
div.topbar {
	display: none !important;
}
body {
	padding-top: 0 !important;
}

/* Ukrycie reklam typu "Ogłoszenie" */
.post_block.hentry.clear.clearfix.column_view:not([id]) {
	display: none !important;
}

/* Nowy górny pasek i nagłówek forum */
#ajax_loading {
	top: 29px !important;
}
#user_navigation {
	position: fixed !important;
	top: 1px;
	right: 8px;
}
#user_navigation, #user_navigation > .ipsList_inline > li > a {
	max-height: 26px !important;
	line-height: 26px !important;
	border: 0 !important;
	box-shadow: none !important;
	z-index: 999999;
}
#admin_bar {
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	z-index: 999999;
	background: rgba(0, 0, 0, 0.7) !important;
	border-bottom: 1px #333333 solid !important;
}
#admin_bar > .right {
	float: left !important;
}
#inbox_link > img, #notify_link > img, span#user_link_dd {
	opacity: 0.5;
	transition: opacity 0.2s linear;
}
#inbox_link > img:hover, #notify_link > img:hover {
opacity: 1;
transition: opacity 0.2s linear;
}
#user_navigation > ul.ipsList_inline > li > a {
	color: #8A8A8A !important;
	transition: all 0.2s linear;
}
#user_navigation > ul.ipsList_inline > li > a:hover {
	text-shadow: 0 0 10px #FFFFFF;
	color: #FFFFFF !important;
	transition: all 0.2s linear;
}
div#admin_bar > ul.ipsList_inline > li > a {
	transition: all 0.2s linear;
}
div#admin_bar > ul.ipsList_inline > li.active > a:hover {
	text-shadow: 0 0 10px #FFFFFF;
	color: #FFFFFF !important;
	transition: all 0.2s linear;
}
.ipsHasNotifications {
	border-radius: 12px !important;
}
.ipsHeaderMenu {
	width: 290px;
}
li.post_count, .left #statusForm, #user_link_menucontent > a > img, #user_navigation > ul.ipsList_inline > li:first-child {
	display: none !important;
}
#primary_nav {
	max-width: 400px !important;
	float: right;
	position: absolute !important;
	top: 44px !important;
	bottom:23px !important;
	left:auto !important;
	right: 38px !important;
}
span.custom_theme {
	position: absolute;
	top: 44px;
	right: 1px;
}
#secondary_navigation {
	margin-top: -54px !important;
}

/* Zmiana koloru zaznaczenia - czarne na białym */
::selection {
	color: /*[[kolorZaznaczTlo]]*/ !important;
	background: /*[[kolorZaznaczTekst]]*/ !important;
}
::-moz-selection {
	color: /*[[kolorZaznaczTlo]]*/ !important;
	background: /*[[kolorZaznaczTekst]]*/ !important;
}

/* Podświetlanie spoilerów po wskazaniu kursorem */
span[style="color:black;background-color:black"], span[style="color:#000000;background:#000000"], span[style="background-color: black"] span[style="color: black"], span[style="color: black"] span[style="background-color: black"] {
	transition: all 0.15s linear;
}
span[style="color:black;background-color:black"]:hover, span[style="color:#000000;background:#000000"]:hover, span[style="background-color: black"] span[style="color: black"]:hover, span[style="color: black"] span[style="background-color: black"]:hover, span[style="color:white;background-color:black"]:hover {
	background-color: /*[[kolorSpoilerTlo]]*/ !important;
	color: /*[[kolorSpoilerTekst]]*/ !important;
	transition: all 0.15s linear;
}

/* Ukrycie tekstu pojawiającego się zamiast avatarów */
.ipsUserPhoto_mini {
	font-size: 0 !important;
}

/* Ukrycie pola "Więcej tematów" */
#topic_stats + h3.maintitle, #topic_stats + h3.maintitle + div {
	display: none !important;
}

/* Naprawa zawijania tekstu przy pisaniu odpowiedzi */
textarea {
	white-space: pre-wrap !important;
}

/* Ukrywanie gravatara */
img[src*=gravatar] {
	display: none !important;	
}

/* Ukrywanie kolorów Akademii */
a.name span[style='color:purple'], a.name span[style='color:gold'] {
	color: inherit !important;	
}
