style.css:
@-moz-document url("http://www.tumblr.com/"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/likes"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/blog/mondoswaifu/followers"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/spotlight/culture"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tagged/") , url("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/blog/mondoswaifu/followers"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/spotlight/culture"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/") {
body {
   background:url('/*[[myimage]]*/')no-repeat fixed  !important;
   background-color: #d97da4 !important;
   background-position: 0% 60% !important;
   background-size: 100% 100%  !important;
 }

div.l-header .tab.iconic a{
  background-color: rgba(249,182,211, 0.75) !important;
  border-radius: 60px !important;  
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Arial; color: #d97da4;
   background: rgba(249,182,211, 0.75) !important;

 }


.notification  {
  	background: #d97da4 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #ffffff !important;
    text-decoration: none !important;
    border: none !important;
  }

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(249,182,211, 0.75) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

} }
