style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com"),
domain("ixquick.com"), domain("startpage.com") {
body, html {
	background: #212121 url("data:image/png;base64,") top left repeat-x !important;
	color: #fff !important;
}

/* defaults for all links */
a:link, a:visited, .link, a font[color] { color: #ccc !important; }
a:active, .link:active, a:active font[color] { color: #fff !important; }
div.clud { color: #6e6e6e !important; }

.web-result:hover,.zero-click-result:hover {
background: none !important;
border:1px solid #ffd !important;
}
.results_links_deep .url, .results_links .url {
color: #C48402 !important; 
}
.snippet {
color: #707070 !important;
}

    .web-result:hover,.zero-click-result:hover {
      cursor: default !important;
      border-color: transparent !important;
}

.zero-click-result { color: #707070 !important; font-size: 115% !important; margin: 0 !important; }
#zero_click_wrapper2 { overflow: hidden !important; }
/* hide sidebar */
DIV#side { display: none !important; }

/* hide footer */
DIV#footer_homepage { display: none !important; }

.results_links_more, .zero-click-result:hover { border: transparent !important; }
.navbutton { font-size: 120% !important; }

}

@-moz-document domain("ixquick.com"), domain("startpage.com") {
SPAN.url { color: #C48402 !important; }
B[style="color:#2D4038"] { color: #6e6e6e !important; }
#ix_promo_box, DIV#tip, A.proxy { display: none !important; }
}
