style.css:
@-moz-document domain("reddit.com") {
body.res-nightmode.with-listing-chooser .listing-chooser .grippy, body.res-nightmode.with-listing-chooser .listing-chooser .grippy:hover {
  background-color: #222!important;
  border-left-color: #333!important;
}

body.res-nightmode.with-listing-chooser .listing-chooser .grippy:hover:before {
  background-color: #222 !important;
}

body.res-nightmode.with-listing-chooser .listing-chooser .grippy:after, body.res-nightmode.with-listing-chooser .listing-chooser .grippy:hover:after {
  border-right-color: #222!important;
}

.res-nightmode .listing-chooser.initialized {
  background-color: #222!important;
}

.res-nightmode .listing-chooser li, .res-nightmode .listing-chooser li.selected {
  background-color: #393939!important;
  color: #ccc!important;
  border-color: #000!important;
  box-shadow: none!important;
  /*box-shadow: -30px 0 30px -15px rgba(20,20,20,0.5) inset,0 2px 6px -1px rgba(0,0,0,0.2);*/
}

.res-nightmode .listing-chooser li a, .res-nightmode .listing-chooser li.selected a {
  color: #8AD!important;
}

.res-nightmode .listing-chooser .intro {
  background-color: #444!important;
  border-color: #222!important;
  color: #ccc!important
}
}
