style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(deviantart.com) {
#searchArea.tang #searchCap {
    background:url(http://img510.imageshack.us/img510/1592/sleekbluery5.gif) no-repeat 0px -199px !important;
}
#searchArea.tang #searchDropdown {
    background:url(http://img510.imageshack.us/img510/1592/sleekbluery5.gif) no-repeat 0px -222px !important;
}
#searchArea.tang #searchDropdown:hover {
    background:url(http://img510.imageshack.us/img510/1592/sleekbluery5.gif) no-repeat 0px -268px !important;
}
#searchArea.tang #searchButton {
    background:url(http://img510.imageshack.us/img510/1592/sleekbluery5.gif) no-repeat -138px -222px !important;
}
#searchArea.tang #searchButton:hover {
    background:url(http://img510.imageshack.us/img510/1592/sleekbluery5.gif) no-repeat -138px -268px !important;
}
#noTouch #searchDropdownOverlay.tang {
    background:url(http://img510.imageshack.us/img510/1592/sleekbluery5.gif) no-repeat -1px -246px !important;
    border-bottom: 1px solid #576960 !important;
}
#noTouch #searchDropdownMenu.tang {
    background:#20CBED url(http://img174.imageshack.us/img174/171/searchdropdownmenubgbluam4.gif) bottom right no-repeat !important;
    border: 1px solid #0A5564 !important;
    border-top: 0px !important;
}
#noTouch #searchDropdownMenu.tang div.sdmItem {
    color: #333333 !important;
}
#noTouch #searchDropdownMenu.tang div.sdmItemHover {
    background-color: #40B7CF !important;
    color: #333333 !important;
}
}
