style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sc2tv.ru") {

  .left-wrapper {
    width: 100% !important;
  }

  #content {
    background: none repeat scroll 0 0 white !important;
  }
  
  .left-column {
    display: none !important;
  }

  #right-content {
    display: none !important;
  }
  
  .center.news {
    width: 990px !important;
  }
  
  .center-header {
    width: 990px !important;
  }
  
  .center-header H2 {
    margin: 0 10px 0 10px !important;
  }
  
  .list-block {
    width: 990px !important;
  }
  
  .list-block > ul {
    width: 990px !important;
  }
  
  .center.news .list-header span.news-time {
    margin: 1px 10px 0 10px !important;
  }
  
  .list-content {
    margin: 0 10px 0 10px !important;
  }
  
  .center.news .list-header span.news-time {
    width: 85px !important;
  }
  
  .center.news .list-header ul {
    width: 865px !important;
  }
  
  #content .center.news .list-content h2 {
    padding: 7px 5px 7px 0 !important;
  }
  
  H2.comments {
    padding-left: 10px !important;
  }
  
  H2.title.ajax-comments-title-processed {
    padding-left: 10px !important;
  }
   
  #comment-form-content {
    padding-left: 10px !important;
  }
  
  #ajax-comments-submit {
    margin-left: 0px !important;
  }
}
