style.css:
@-moz-document domain("colg.cn") {
.sign{display: none!important;}/*隐藏签名*/
.hdc{display: none!important;}/*隐藏论坛顶部横幅*/
.avatar{display: none!important;}/*隐藏头像*/
.rs_main{display: none!important;}/*隐藏*/
div[class="hm cl"]{display: none!important;}/*隐藏分享*/
.fullvfastpost{margin-top: 0px!important;}/*调整快速回复*/
#vmessage{background-color: #FFFFFF!important;color: #000000!important;}/*调整快速回复框内颜色*/
.mbm {margin-bottom: 0 !important;}/*调整快速回复*/
.mtn {margin-top: 0 !important;}/*调整快速回复*/
.pbg2{display: none!important;}/*隐藏经验条*/
.pil{display: none!important;}/*隐藏威望存在感精华*/
/*.pbn{display: none!important;}隐藏帖子顶栏标题*/
div[class="bm bml pbn"]{display: none!important;}/*隐藏版块模块标题*/
div[class="mtw mbw"]{display: none!important;}/*隐藏列表相关帖子*/
div.drag{display: none!important;}/*隐藏吗啡屋版块内容推荐*/
.dfsj_type{border: 1px solid #9DB3C5!important;}/*美化版块分类边框*/
.wp{min-width:720px!important;}/*调整页面最小宽度*/
.md_ctrl{display: none!important;}/*隐藏勋章*/
.dfsj_scfx {
height: auto!important;
width: auto!important;
margin: auto!important;
}/*减少收藏、分享按钮所占高度*/
.dfsj_scfx a{
background: none!important;
padding: 0!important;
}/*取消收藏、分享按钮背景*/
img[src="source/plugin/mobileoem/static/image/push.png"]{display:none!important}/*取消穿越按钮图案*/
.pct {
    padding-bottom:0!important;
}/*减少收藏、分享按钮所占高度*/
/*img{height:auto!important;}调整所有图片高度为自适应,影响首页模块高度*/
.t_fsz{min-height: 10px!important;}/*调整帖子内容最小高度*/
#scbar{display: none!important;}/*隐藏论坛首页搜索框*/
#vfastpostform{display: none!important;}/*隐藏快速回复*/
.xg2,.psth{display: none!important;}/*隐藏积分*/
.psta img{display: none!important;}/*隐藏点评背景*/
.quote,blockquote{margin:0!important;background:none!important;padding:0!important;}/*隐藏引用背景*/
li.pm2{display: none!important;}/*隐藏帖内发送消息*/
#get-envelope-sign{display: none!important;}/*隐藏红包*/
p.xg1{display: none!important;}/*隐藏自定义头衔*/
#bbs-right-nav{display: none!important;}/*隐藏右侧悬浮导航*/
.vip-level-name.vip-2{display: none!important;}/*隐藏豆芽*/
.vip-level-name.vip-1{display: none!important;}/*隐藏豆芽*/
.icon-mobile.mobile-type-2{display: none!important;}/*隐藏手机标志*/
.icon-mobile.mobile-type-1{display: none!important;}/*隐藏手机标志*/
.vip-medal{display: none!important;}/*隐藏勋章*/
.vip-level-icon{display: none!important;}/*隐藏VIP图标*/
.pstl{padding:0}
.vip-level-name,.pls p, .pls .pil, .pls .o{margin:0px 10px 0px 20px}
.pi{margin-bottom: 0px}
/*[[mode]]*/
}

options.value:
.t_f img{opacity:0}
.t_f img:hover{opacity:1}/*贴内图片默认隐藏，鼠标悬停自动显示*/
.savephotop img{opacity:0}
.savephotop img:hover{opacity:1}/*附件图片默认隐藏，鼠标悬停自动显示*/
