style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('^(?!http://.*rorichan\\.org/.*/src/)http://.*rorichan\\.org/.*$') {

/* Cleanup */
.boardlist.bottom, th, img.hidden
{   display: none !important; }

*
{ padding: 0 !important;}
/* Misc */

html body
{   margin: 25px auto auto auto !important;
    width: 99%;
    background-image: none !important;}
    
hr
{   z-index: 0;
    opacity: .4;
    max-width: 500px }

/* Banner Area */
img.banner
{   position: absolute;
    top: 12px; right: 3px}

header
{   position: absolute !important;
    top: 113px; 
    width: 100%;
    }
    
.subtitle, h1
{   right: 3px;
    width: 300px;
    margin-left: auto !important; margin-right: 0 !important; }

/* Reply Box */
tbody
{   
    top: 25px; left: 10px;
    z-index: 99;
    /*border: 1px solid rgba(0,0,0,.3);*/ }

table
{  position: relative;
    min-width: 466px;
    max-width: 466px !important;
    margin-left: auto !important; margin-right: auto !important;
    border: 1px solid rgba(0,0,0,.3);}
    
    
div.banner
{   width: 320px; padding-bottom: 1px !important;
    margin-left: auto !important ; margin-right: auto !important;}
    
input[name="name"]
{   /*background: url("data:image/jpeg;base64,") no-repeat right;*/
    position: absolute;
    top: 3px; left: 3px;
    height: 24px !important; width: 150px}
    
input[name="email"]
{   position: absolute;   
    top: 3px; left: 156px;
    height: 24px !important; width: 150px}
    
input[name="subject"]
{   position: absolute;   
    top: 3px; left: 310px;
    height: 24px !important; width: 150px}

input[name="post"]
{   position: absolute;
    width: 75px; height: 23px !important;
    bottom: 3px; right: 3px;
    background-color: white !important;
    -moz-appearance: none !important;
    border: 1px solid rgba(0,0,0,.3); }
    
input[name="file"]
{   position: absolute;
    bottom: 27px; left: 3px;
    width: 456px; height: 22px !important;
    padding: 3px 0px 1px 1px !important;
    border: 1px solid rgba(0,0,0,.3); }
    
label[for="spoiler"]
{   position: absolute;
    bottom: 3px; left: 3px;
    padding: 3px 0px 3px 20px !important;
    width: 90px !important; }
    
input[id="spoiler"]
{   position: absolute; 
    bottom: 7px !important; left: 6px;
    z-index: 1
 }

html body form table tbody tr td input[name="password"]
{   position: absolute;
    height: 21px !important; width: 120px;
    bottom: 3px; left: 115px; }
    
html body form table tbody tr td span.unimportant 
{   position: relative;
    left: 236px; bottom: 3px; }

textarea#body
{   position: relative;
    left: 0px; top: 22px;
    max-width: 452px; min-height: 100px;
    min-width: 452px; height: 100px;
    border: 1px solid transparent !important;
    margin-bottom: 54px !important}
    
/* Post Sizes and Styles */
.post-hover 
{   width: auto !important; }

.post.reply 
{   z-index: 1;
    width: 100%;
    border: 1px solid rgba(0,0,0,.3) !important;
    padding-bottom: 4px !important; }

.highlighted.post-hover, .post.reply.highlighted 
{   border: 1px solid rgba(0,0,0,.3); }

/* Navbar */
.boardlist 
{   position: fixed;
    border: 1px solid rgba(0,0,0,.3);    
    width: 100%;
    margin-left: -8px;
    top: -3px;
    text-align: center; }

#toggle-images 
{   position: fixed;
    top: 0; right: 3px; }
/* Variables */

/*Background*/
html, body 
{   background-color: /*[[background]]*/ !important; }

/* Background Font Color */
header
{   color: /*[[bfontcolor]]*/; }

/*Foreground*/
post.reply, .post-hover, /*tbody,*/ table, .boardlist, input[name="file"], label[for="spoiler"]
{   background-color: /*[[foreground]]*/ !important; }

/* Foreground Font Color */
.post.reply, .post-hover, .highlighted.post-hover, .post.reply.highlighted, .boardlist, .banner, hr
{   color: /*[[ffontcolor]]*/ !important; }

/*Highlight*/
.highlighted.post-hover, .post.reply.highlighted, .banner 
{   background-color: /*[[highlight]]*/ !important; }

/*Border Radius*/
.post.reply, .post-hover, .highlighted.post-hover, .post.reply.highlighted, textarea#body, input
{   border-radius: /*[[radius]]*/ !important; }

}
