style.css:
@-moz-document domain("cab.vn"), domain("beta.cab.vn") {
body{
color: #ffffff !important;
background: url("http://i.imgur.com/i5B9hk3.jpg") repeat fixed center center #000000 !important;
}
a, p{
  
color: #ffffff !important;
text-shadow: 5px 5px 10px #5f5f5f !important;
}
.info-post:hover{
opacity:1.0 !important;
}
header{
border-bottom: 1px solid #ffc0c0 !important;
}
legend, span{
color: #ffffff !important;
text-shadow: 5px 5px 10px #5f5f5f !important;


}
.navbar, .navbar-inner, .vote-score{
  	color: #fff !important;
    background-color: #ff7878 !important;
	opacity:0.8 !important;
    border-bottom: 1px solid #ff7878 !important;
  font-size: 11px;
}
.container-fluid {
    width: 1010px !important;
      margin: 0 auto;
}
#topnav .container {
width: 1010px;
}
    .list-header{
  background: #ffc0c0 !important;
	opacity:0.7 !important;
    color: #fff !important;
	

}
.content-post, .info-post {
      background: #ffc0c0 !important;
          color: #fff !important;
         
    opacity:0.86 !important;
}
.content-body, .inner, .row-fluid{
  background: transparent !important;
}
.user-single-page {
      background: #ffc0c0 !important;
