    color: #fff !important;
}

.result-post, .type-post{
background: #212 !important;
	opacity:0.6 !important;
    color: #fff !important;
	text-shadow: 1px 1px #121212 !important;
  border-top: 5px solid #ffc0c0 !important;
  border-bottom: 5px solid #ffc0c0 !important;
}
  
  .back-to-top {
