style.css:
/* Created by Cameron Valk (valk.cameron@gmail.com) */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("about:addons"),
               url("chrome://mozapps/content/extensions/extensions.xul") {

/* To customize button text, change 'Restart Firefox' to your desired phrase. */
#updates-restart-btn:before {
content: 'Restart Firefox' !important;
}

/* Hides the default text displayed when #updates-restart-btn is called */
#updates-restart-btn .button-text {
display: none !important;
}

/* Change the settings below to customize button font, borders, position, and colors */
#updates-restart-btn {
display: -moz-box !important;
border-color: rgba(31, 64, 100, 0.4) !important;
border-radius: 2px !important;
border-style: solid !important;
border-width: 1px !important;
text-decoration: none !important;
color: #363636 !important;
width: 85px !important;
height: 22px !important;
font-weight: 600 !important;
padding-top: 1px !important;
padding-bottom: 0px !important;
padding-left: 3px !important;
padding-right: 1px !important;
margin-top: 0px !important;
margin-bottom: 0px !important;
margin-left: 0px !important;
margin-right: 3px !important;
}



}
