style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http://www.metrolyrics.com/([a-z]|[a-z][a-z\-]*[a-z])-lyrics-([a-z]|[a-z][a-z\-]*[a-z]).html") {
	html > body > #page #lyrics > .row {
		width: 480px !important;
	}
	html > body > #page #lyrics > #lyrics-main > .left-panel {
		float: initial !important;
	}
	html > body > #page #lyrics > #lyrics-main > .left-panel > .lyrics {
		width: initial !important;
	}
	html > body > #page #lyrics > #lyrics-main > .right-panel,
	html > body > #page #lyrics > #similarArtists{
		display: none !important;
	}
}
