style.css:
@-moz-document domain("smashingmagazine.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.article--grid__note,
.h3,

h3
{
	text-transform: capitalize!important;
}

.subnav .nav-item-link,
.subnav-item--active a.nav-item-link,
.subnav-item--active a.nav-item-link:hover,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.feature-comparison,

tr
{
    border: 1px solid #f20!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

selector
{
	font-size: 2.5rem!important;
}

selector
{
	font-size: 1.5rem!important;
}

selector
{
	font-size: 1.2rem!important;
}

selector
{
	line-height: 2.5rem!important;
}

selector
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.c-garfield-the-cat>.c-promo-box
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.main-header {
	margin-bottom: 7rem!important;
}

.featured-articles__teaser {
	margin-top: -6rem!important;
}

.featured-article__tags {
	margin: 3em 0!important;
}

.article--grid__read-more {
	bottom: -36px!important;
}

.article__content .books__book__meta {
	grid-column-end: 1!important;
	grid-row-start: 4!important;
}

.c-garfield-the-cat {
	grid-column-end: 15!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.article--post,
.col--article--grid,
.col--article--meow__membership.article--grid,
.event__highlights__image-grid img,
.featured-article,
.featured-articles-container,

section {
	margin: 12px!important;
}

.job {
	margin: 12px 0!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.article--post,
.article-container .author--full,
.col--article--grid,
.col--article--meow__membership.article--grid,
.featured-article,
.featured-articles-container,

section {
	padding: 12px!important;
}

.vertical span {
	padding: 0 7px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.article--post__title,
.c-person-of-the-week .author__desc p,
.c-garfield-the-cat,
.container {
	max-width: none!important;
}

.article--post__stats {
	width: auto!important;
}

.col-6 {
	width: 100%!important;
}

.container {
	width: 92%!important;
}

.article--post__content {
	width: 85%!important;
}

.col--article--grid {
	width: 33%!important;
}

.col--article--grid,
.featured-article {
	width: 31.33%!important;
}

.event__highlights__image-grid img {
	width: 30.33%!important;
}

.family__grid .promo-box {
    flex: 1 0 calc(19% - 1vw);
}

.c-garfield-the-cat {
    grid-column-gap: 0.75rem;
}
.feature-panel-container {
    grid-column: 3/15!important;
}
}

@-moz-document domain("smashingmagazine.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
	.c-garfield-the-cat {
		grid-template-columns: 18.5rem repeat(11,minmax(0,1fr)).7fr .3fr 22.5rem;
		grid-column-gap: 1.75rem;
	}

	.row {
		margin-left: -31rem;
	}

}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

