@-moz-document domain("smashingmagazine.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.article--grid__title a,a,
.conf-panel--blue,
.job:hover
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.book-cta__title span,
.feature-comparison .feature-comparison__category,
.right-lined span
{
	background: #060505!important;
	background-color: #060505!important;
}

header.subnav__header
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#main,

.subnav .nav-item-link,
.alert small,
.article--grid:hover .article--grid__read-more,
.article--grid:not(.col--article--meow):hover::before,
.article--grid__read-more,
.books__book__wrapper,
.books__tabs,
.btn--white:focus,.btn--white:hover,
.cover,
.featured-articles__teaser span,
.internal__page__wrapper,
.language-css .token.string,
.membership__discount small,
.style .token.string,
.token.entity,
.token.operator,
.token.url,
.toolbar-item:focus, .toolbar-item:hover,

footer
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

selector
{	
	background: radial-gradient(circle, #031017 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

selector::before
{
	background: radial-gradient(circle, #000 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

selector:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.btn,
.btn--white,
.toolbar-item
{	
	background: radial-gradient(circle, #380002a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn::before,
.btn--white::before,
.toolbar-item::before
{
	background: radial-gradient(circle, #000 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn:hover::before,
.btn--white:hover::before,
.toolbar-item:hover::before {
  opacity: 1!important;
}

/* Light Blue 
_____________________________________________________________________________________________________*/

