style.css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {


#posts .post:after {
  content: '';
  opacity: 0;
  -webkit-transition: opacity .2s .2s linear;
  transition: opacity 1s 1s linear;
background: url('http://i85.photobucket.com/albums/k57/mconfer/baelhat_zpsf925d6a9.png')!important;
background-size: 100% 100% !important;
position: absolute !important;
width: 91px !important;
height: 63px !important;
top: -30px !important;
right: 545px !important;
}
#posts .post:hover:after {
  opacity: 1;
  -webkit-transition: opacity 1s linear;
  transition: opacity 1s linear;
}

}
