style.css:
@-moz-document url("http://www.gaiaonline.com/marketplace/vendsearch/?sortBy=91") {
        .goldMessageText ,
        .goldCoin ,
        .header_content h1 ,
        .header_content .gaiaLogo,
        .header_content .imgAvatar,
        .header_content #cashtree,
	#vend_listings h2 ,
        #shops_landing_tabs ,
	#display_options strong ,
        #pagination ,
        #gaia_footer ,
	#display_options>span {
		display:none !important; 
		background:none !important;
	}

        #gaia_header .header_content {
                height: 0px;
        }

        .hud-sprite padded-icon gold-icon {
                position: absolute;
        }

        #display_options {
                width: 250px;
                float: left;
        }

        td.vend_rows_ex_buy .transaction_button {
                position: absolute;
                height: 28px;
        }

        .auction-listings strong {
                position: absolute;
                text-align: left;
                margin: 0px 0px 0px -110px;
        }

        #gaia_header .header_content .notificationChanges{
                top: 100px;
                left: 1000px;
        }
}

@-moz-document url("http://www.gaiaonline.com/marketplace/userstore") {
        .marketplaceinputfield {
                height: 50px;
        }
}
