style.css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body {
   background: url /*[[vocaloid]]*/ bottom right no-repeat fixed  !important;
   background-color: rgba(97,193,190, 0.95) !important;
 }#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(27, 123, 120, 0.20) !important;
 }
 #new_post {
   background-color: rgba(114, 254, 116, 0.40) !important;
 }


#posts .new_post_label .new_post_label_icon {
background-image: url('http://i.imgur.com/mvJbM.png') !important;
margin-top: 20px !important;
height: 94px !important;
width: 80px !important;
opacity: 75 !important;
}

#posts .post.new_post {
background: transparent url('http://media.tumblr.com/7870a0525f6cad153d92b14adf62d883/tumblr_inline_mt3usoCzul1rftn84.png') no-repeat bottom !important;
background-position: center !important;
}

div.l-content #new_post .new_post_label {
color: rgba(226,224,209,0.80) !important;
line-height: 3.9em;
font-weight: bold !important;
font-size: .85em !important;
}

div.l-header #home_button > a {
    background-image:url('http://media.tumblr.com/60e4566b202e29c98ed7ad6b12511c02/tumblr_inline_mt3v75OOaf1rftn84.png');
    background-size: 36px 36px;
  }

#return_to_top .return_to_top_icon{
background: url('http://media.tumblr.com/60e4566b202e29c98ed7ad6b12511c02/tumblr_inline_mt3v75OOaf1rftn84.png')
     no-repeat transparent!important;
   background-size: 100% 100%  !important;
  
}

#logo {
       height: 0 !important;
       width: 0 !important;
       padding-left: 260px !important;
       padding-top: 70px !important;
       background: url(http://media.tumblr.com/36face7e6f81ba8590f4a4b7f9a497d8/tumblr_inline_mt3t3sYA2B1rftn84.png) no-repeat !important;

}
}
