style.css:
@-moz-document domain("crysis.com"), domain("www.crysis.com") {
.cry-row-background {
    display: none !important;
  height: 0px !important;
}

.cry-cell {
    padding: 0 0 0 0 !important; 
}

h3, body h3, .cry-forums h3, .cry-forums h2, .cry-main ul.cry-table > li .cry-cell, .cry-main .cry-frame .cry-login-box, .cry-main .cry-topic-header > div .cry-login-box, .cry-main .cry-topic-footer > div .cry-login-box {
  	text-transform: none;
	text-transform: none !important;
}
}
