style.css:
@-moz-document url-prefix("http://userstyles.org/users/") , url-prefix("https://userstyles.org/users/") {
table.author-styles > tbody > tr > td:nth-of-type(1) {width: 250px !important;}
.date-value {width: 90px !important;}
.numeric-value:nth-of-type(1) {width: 60px !important;}
.numeric-value:nth-of-type(2) {width: 62px !important;}
table.author-styles > tbody > tr > td:nth-of-type(7)  {width: 135px !important;}
}
