@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Samples
    Orange Firefox Logo        https://blog.mozilla.org/addons/wp-content/themes/addons-nova/img/favicon.ico#-moz-resolution=16,16
    M on Transparent           https://blog.mozilla.org/security/wp-content/themes/OneMozilla/img/favicon.ico#-moz-resolution=16,16
    M on Transparent           https://mozorg.cdn.mozilla.net/media/img/favicon.ico
    M on Transparent           https://wiki.mozilla.org/skins/common/images/favicon.ico#-moz-resolution=16,16
    M on Transparent           https://blog.mozilla.org/favicon.ico#-moz-resolution=16,16
    M on Transparent           https://blog.mozilla.org/ux/wp-content/themes/OneMozilla/img/favicon.ico#-moz-resolution=16,16
    M on Transparent           https://location.services.mozilla.com/favicon.ico#-moz-resolution=16,16
    M on Transparent           https://blog.mozilla.org/wp-content/themes/OneMozilla/img/favicon.ico#-moz-resolution=16,16
 */

.tab-content > .tab-icon-image[src*="blog.mozilla.org/wp-content/themes/OneMozilla/img/favicon.ico"],
.tab-content > .tab-icon-image[src*="blog.mozilla.org/security/wp-content/themes/OneMozilla/img/favicon.ico"],
.tab-content > .tab-icon-image[src*="mozorg.cdn.mozilla.net/media/img/favicon.ico"],
.tab-content > .tab-icon-image[src*="wiki.mozilla.org/skins/common/images/favicon.ico"],
.tab-content > .tab-icon-image[src*="blog.mozilla.org/favicon.ico"],
.tab-content > .tab-icon-image[src*="location.services.mozilla.com/favicon.ico"],
.tab-content > .tab-icon-image[src*="blog.mozilla.org/ux/wp-content/themes/OneMozilla/img/favicon.ico"] {
  background-color: #999999!important;
  border-radius: 1px !important;
}

.tab-content > .tab-icon-image[src*="media.wnyc.org/static/img/favicon_onthemedia.ico"],
.tab-content > .tab-icon-image[src*="media.wnyc.org/static/img/favicon_radiolab.ico"],
.tab-content > .tab-icon-image[src*="persona.org"],
.tab-content > .tab-icon-image[src*="eff.org"],
.tab-content > .tab-icon-image[src*="dailymail.co.uk"],
.tab-content > .tab-icon-image[src*="theregister.co.uk"],
.tab-content > .tab-icon-image[src*="github.com"],
.tab-content > .tab-icon-image[src*="washingtonpost.com"],
.tab-content > .tab-icon-image[src*="nytimes.com"],
.tab-content > .tab-icon-image[src*="inc.com"],
.tab-content > .tab-icon-image[src*="craigslist.org"],
.tab-content > .tab-icon-image[src*="thedissolve.com"] {
  background-color: #bbbbbb !important;
  border-radius: 1px !important;
}
