style.css:
@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {
/* 共通設定
----------------------------------------------- */

html,
body {
	font-size: 12px !important;
	background: #fff !important;
	min-width: 980px !important;
}
#videoInformationWrapper,
#tagRelatedBanner,
#videoHeaderTagList .nicoruWrapper,
#videoHeader .videoMenuToggle,
#topVideoInfo .ranking > .icon:first-child,
.channel .ch_breadcrumb,
#playerTabContainer .socialButtons,
.comment-panel .nicoru-button,
#outline .videoMainInfoContainer > *:not(#videoInfoHead):not(#videoShareLinks):not(#bottomVideoDetailInformation),
#outline.owner #videoInfoHead > *:not(.videoEditMenu),
#outline:not(.owner) #videoInfoHead,
#playlist .fadeout,
#playlistContainerInner .playlistItem .balloon .arrow,
#videoReviewComment .commentUserProfile,
#videoReviewComment .panelTrigger,
#ichibaMain .ichiba_mainitem > .nicoru,
#topVideoInfo .hiddenInfo,
#bottomVideoDetailInformation .trigger,
#nicommend .nicommendHeader > .description,
#ichibaMainFooter > .commandArea2,
#ichibaMainFooter > .info,
#ichibaMainFooter > .associate {
	display: none !important;
}
#superBanner,
#rectangleAd,
#sideFollowAd,
#pageFooterAds,
#videoReviewBottomAd,
#playerBottomAd,
#selectionSideAd,
#videoExplorerSideFollowAd,
#playerTabContainer .playerTabAds,
#videoExplorer .videoExplorerFooterAdsContainer,
#playerTabContainer .nicoSpotAds,
#tagRelatedBannerContainer {
	display: none !important;
}
/* ヘッダー
----------------------------------------------- */

#siteHeaderInner {
	width: 980px !important;
	height: 32px !important;
}
#siteHeaderInner > ul li {
	height: 32px !important;
	line-height: 32px !important;
}
#siteHeaderInner > ul li:hover {
	background: none !important;
}
#siteHeaderInner > ul > li > a {
	margin: 0 !important;
	color: #eee !important;
	font-size: 12px !important;
}
#siteHeaderInner > ul > li:not([id]) > a:hover {
	color: #fff !important;
	background: #3e3e3e !important;
}
#siteHeaderInner > ul > li:not([id]) > a > span {
	border: none !important;
	color: #eee !important;
}
#siteHeaderInner > ul > li:not([id]) > a:hover > span {
	color: #fff !important;
}
#siteHeaderInner > ul > li > ul {
	top: 31px !important;
	border: none !important;
	box-shadow: 0 1px 3px rgba(0, 0, 0, .5) !important;
	background: #fff !important;
	border-top: 1px #272727 solid !important;
	padding: 0 !important;
}
#siteHeaderLeftMenuContainer > li > a {
	color: #333 !important;
	padding: 0 7px 0 8px !important;
	margin: 0 !important;
}
#siteHeaderInner > ul > li > ul > li > a:hover {
	background: #eee !important;
}
#siteHeaderLeftMenu > a,
#siteHeaderRightMenu > a {
	border-left: 1px transparent solid !important;
	border-right: 1px transparent solid !important;
}
#siteHeaderLeftMenu:hover > a,
#siteHeaderRightMenu:hover > a {
	background: #fff !important;
	position: relative !important;
	z-index: 10000 !important;
	border-right-color: rgba(0, 0, 0, .65) !important;
	border-left-color: rgba(0, 0, 0, .65) !important;
}
#siteHeaderLeftMenu:hover > a > span,
#siteHeaderRightMenu:hover > a > span {
	color: #333 !important;
}
#siteHeaderLeftMenuContainer {
	width: 231px !important;
	left: 1px !important;
}
#siteHeaderLeftMenuContainer > li:not([class]):nth-child(2n+1) {
	border-right: 1px #e9e9e9 solid !important;
}
#siteHeaderLeftMenuContainer > .menuServiceList {
	width: 231px !important;
	border-top: 1px #e9e9e9 solid !important;
}
#siteHeaderLeftMenuContainer > .menuServiceList > a {
	width: 212px !important;
	display: block !important;
}
#siteHeaderRightMenuContainer {
	right: 1px !important;
}
#siteHeaderRightMenuContainer > li > a {
	color: #333 !important;
	padding: 0 10px !important;
	width: 110px !important;
}
#siteHeaderUserIconContainer {
	background: none !important;
	margin: 0 !important;
}
#siteHeaderUserIconContainer {
	width: 25px !important;
	height: 25px !important;
	line-height: 25px !important;
}
#siteHeaderUserIconContainer > img {
	width: 25px !important;
	height: 25px !important;
	margin: 0 !important;
	border-radius: 2px;
}
#siteHeaderNotification span.siteHeaderNotNotifications {
	margin: 0 !important;
}
#siteHeaderRightMenuUnfix .siteHeaderScroll,
#siteHeaderRightMenuFix .siteHeaderFix {
	height: 18px;
	line-height: 18px !important;
	margin-top: 7px;
}
/* ヘッダーコンテンツ
----------------------------------------------- */

body #content {
	padding-top: 32px !important;
}
body.size_normal:not(.full_with_browser) .videoHeaderOuter {
	margin: 0 auto !important;
	width: 1201px !important;
}
#videoHeader {
	width: 960px !important;
	padding: 0 10px !important;
}
body.videoExplorer #videoHeader {
	margin-bottom: 10px !important;
}
#videoHeader.menuOpened #editorMenu {
	margin-top: 12px !important;
}
#videoHeader.menuOpened .tvChanMenuHeightController {
	margin-top: 0 !important;
}
#videoHeader.owner.ownerThreadEditor #editorMenu {
	transition: none !important;
	width: auto !important;
	display: inline-block !important;
}
#parentMenuBox > li:first-child {
	border: none !important;
	background-color: #2d2d2d !important;
	padding: 0 5px !important;
}
#parentMenuBox > li:first-child > span {
	color: #fff !important;
}
#videoHeaderMenu {
	width: 100% !important;
	float: none !important;
	margin: 0 !important;
	padding-top: 10px !important;
}
#videoHeaderMenu .searchContainer {
	position: static !important;
	margin: 0 !important;
}
#videoHeaderMenu .searchOptionButton {
	padding: 5px !important;
	margin: 0 !important;
}
#videoHeaderMenu .searchContainer > form {
	position: relative !important;
}
#videoHeaderMenu .searchContainer > form:before {
	content: '';
	width: 160px;
	height: 36px;
	background: url(data:image/svg+xml;base64,) no-repeat;
	display: inline-block;
	margin-right: 30px;
}
#videoHeaderMenu .searchContainer .searchOption {
	top: 36px !important;
	left: 190px !important;
	border-color: #ccc !important;
	width: 215px !important;
	padding: 0 !important;
	background-color: #fff !important;
	box-shadow: 0 1px 2px rgba(0, 0, 0, .1) !important;
	z-index: 1 !important;
}
#videoHeaderMenu .searchContainer .searchOption > ul > li > a {
	line-height: 2em !important;
	padding: 0 !important;
	display: block !important;
}
#videoHeaderMenu .searchContainer .searchOption > ul > li > a:hover {
	background-color: #eee !important;
}
#videoHeaderMenu .searchContainer .searchOption > ul > li > a > span {
	margin-left: 10px !important;
}
#videoHeaderMenu .searchText {
	width: auto !important;
	height: auto !important;
	background: #fff !important;
	border-radius: 0 !important;
	box-shadow: none !important;
	padding: 0 !important;
	display: inline-block !important;
}
#videoHeaderMenu .searchText:hover {
	border-color: #ccc !important;
}
#videoHeaderMenu .searchText > input[type=text] {
	width: 180px !important;
	margin: 0 !important;
	padding: 5px !important;
	display: block !important;
}
#videoHeaderMenu .searchText > input[type=text]:hover,
#videoHeaderMenu .searchText > input[type=text]:focus {
	box-shadow: 0 1px 1px rgba(0, 0, 0, .06) inset !important;
}
#videoHeaderDetail {
	width: 100% !important;
	margin: 0px !important;
	background-position: 50% !important;
	padding: 28px 0 10px !important;
}
#videoHeaderDetail .videoDetailExpand {
	height: auto !important;
	min-height: 0 !important;
	width: 850px !important;
	padding: 0 !important;
}
#videoHeaderDetail h2 {
	max-width: 100% !important;
	display: block !important;
	pointer-events: none;
}
#videoHeaderDetail h2 .videoHeaderTitle {
	font-size: 20px !important;
}
#topVideoInfo .infoHeadOuter {
	font-size: 12px !important;
	font-weight: bold !important;
	position: absolute !important;
	top: 60px !important;
	left: 10px !important;
}
#videoHeader.ownerThreadEditor #topVideoInfo .infoHeadOuter,
#videoHeader.ownerThreadEditor #topVideoInfo .ranking {
	top: 138px !important;
}
#topVideoInfo .parentVideoInfo .contentsTree {
	text-align: center !important;
	width: 57px !important;
	padding: 0 10px !important;
	line-height: 2.1em !important;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	-webkit-text-overflow: ellipsis;
	vertical-align: bottom;
	position: absolute;
	top: 10px;
	left: 10px;
	background-color: #111;
	color: #fff;
}
#topVideoInfo .parentVideoInfo .videoThumb {
	width: 73px !important;
	height: 41px !important;
	overflow: hidden !important;
	border: 2px #111 solid !important;
	border-top: none !important;
	margin: 33px 10px 0 0 !important;
}
#topVideoInfo .parentVideoInfo .videoThumb img {
	max-width: 73px !important;
	height: auto !important;
	margin-top: -7px !important;
}
#topVideoInfo .postNameSubmit,
.videoPostedAt {
	color: #111 !important;
}
#topVideoInfo .videoDescription {
	width: 640px !important;
	margin: 0 !important;
	float: left !important;
}
#topVideoInfo .videoDescription {
	border: 2px #333 solid !important;
	border-bottom: none !important;
	padding: 10px !important;
}
#topVideoInfo .userName {
	color: #333 !important;
	font-weight: bold !important;
	text-decoration: underline !important;
}
#topVideoInfo .showOtherVideos {
	color: #333 !important;
}
#topVideoInfo .userName:hover {
	text-decoration: none !important;
}
#topVideoInfo .userProfile,
#topVideoInfo .ch_prof {
	width: 260px !important;
	background-color: #e9efef !important;
	border: 2px #999f9f solid !important;
	padding: 10px !important;
	margin: 0 !important;
}
#topVideoInfo .userIconContainer,
#ch_prof > .symbol {
	width: 48px !important;
	border: 2px #333 solid !important;
}
#topVideoInfo .usericon,
#topVideoInfo .symbol > img {
	width: 48px !important;
	border: none !important;
	box-shadow: none !important;
}
#topVideoInfo .profile {
	width: 195px !important;
	padding-left: 13px !important;
}
#topVideoInfo .ch_prof p.bread,
#topVideoInfo .ch_prof h6 {
	font-size: 12px !important;
	white-space: nowrap !important;
	overflow: hidden !important;
	text-overflow: ellipsis !important;
	-webkit-text-overflow: ellipsis !important;
}
#topVideoInfo .favVideoOwner,
#topVideoInfo .channelFavoriteLink {
	background-image: -moz-linear-gradient(top, #222222, #000000) !important;
	background-image: -webkit-linear-gradient(top, #222222, #000000) !important;
	background-image: linear-gradient(top, #222222, #000000) !important;
	border: none !important;
	border-radius: 10px !important;
	margin: 3px 0 0 !important;
}
#topVideoInfo .isNotFavoritedText,
#topVideoInfo .isFavoritedText {
	color: #fff !important;
}
#topVideoInfo .ranking {
	font-size: 12px !important;
	position: absolute !important;
	top: 60px !important;
	left: 205px !important;
	padding: 0 !important;
	margin: 0 !important;
	border: none !important;
}
#topVideoInfo .ranking:before {
	content: '\002026';
	font-weight: bold;
	margin: 0 5px;
}
#videoHeader .videoInformation .dicIcon {
	position: absolute !important;
	top: 90px !important;
	right: 10px !important;
}
#topVideoInfo .dicIcon .dic {
	background: none !important;
	padding: 0 !important;
	display: inline !important;
}
#topVideoInfo .dicIcon .dic:before {
	content: '';
	width: 13px;
	height: 13px;
	padding-right: 4px;
	display: inline-block;
	vertical-align: middle;
}
#topVideoInfo .hiddenInfo {
	width: 260px !important;
	background-color: #fff !important;
	border: 2px #999f9f solid !important;
	padding: 10px !important;
	margin: 0 !important;
	position: absolute !important;
	right: 0 !important;
	top: 233px !important;
	z-index: 2 !important;
}
#topVideoInfo .videoMainInfoContainer {
	width: 664px !important;
	border: none !important;
	padding: 0 !important;
	display: block !important;
}
#topVideoInfo .parentVideoInfo {
	width: 640px !important;
	padding: 0 10px 10px !important;
	border: 2px #333 solid !important;
	border-top: none !important;
	border-bottom: none !important;
	margin: 0 !important;
	background-color: #fff !important;
	float: none !important;
	position: relative !important;
}
#topVideoInfo .parentVideoInfo .cct {
	border-top: 1px #ccc solid !important;
	margin: 0 !important;
	padding-top: 15px !important;
}
#topVideoInfo .parentVideoInfo .parentVideoTitle {
	margin: 5px 0 0 !important;
}
#videoHeader .toggleDetailExpand {
	margin: 0 !important;
	float: none !important;
	position: absolute !important;
	right: 10px !important;
	top: 45px !important;
}
#videoHeader.infoActive .videoDetailOpenButton,
#videoHeader:not(.infoActive) .videoDetailCloseButton {
	display: none !important;
}
#videoHeader .toggleDetailExpand > .videoDetailCloseButton,
#videoHeader .toggleDetailExpand > .videoDetailOpenButton {
	width: 200px !important;
	height: 26px !important;
	text-indent: -999px !important;
	overflow: hidden !important;
	display: block;
}
#videoHeader .toggleDetailExpand > .videoDetailOpenButton {
	background: url(data:image/png;base64,) no-repeat !important;
}
#videoHeader .toggleDetailExpand > .videoDetailCloseButton {
	background: url(data:image/png;base64,) no-repeat !important;
}
#topVideoInfo > .videoMainInfoContainer > div:not([class]) {
	border-bottom: 2px #333 solid !important;
}
#topVideoInfo .hiddenInfo .videoShareBody .blogLinksWrap {
	width: 100% !important;
	float: none !important;
}
#topVideoInfo {
	margin: 0 !important;
}
#topVideoInfo .videoStats {
	font-size: 10px !important;
	color: #fff !important;
	width: 190px !important;
	border: 1px #000 solid !important;
	margin: 0 !important;
	position: absolute !important;
}
#topVideoInfo .videoStats > li {
	color: #fff !important;
	padding: 0 5px !important;
	margin: 0 !important;
	background-image: -moz-linear-gradient(top, #393939, #111, #393939) !important;
	background-image: -webkit-linear-gradient(top, #393939, #111, #393939) !important;
	background-image: linear-gradient(top, #393939, #111, #393939) !important;
	border: none !important;
	border-bottom: 1px #000 solid !important;
	border-left: none !important;
	display: block !important;
	float: none !important;
	line-height: 17px !important;
	height: 17px !important;
	position: relative !important;
}
#topVideoInfo .videoStats > li.videoStatsMylist {
	border-bottom: none !important;
}
#topVideoInfo .videoStats > li > span {
	color: #fff !important;
	padding: 0 !important;
	display: block !important;
	position: absolute !important;
	right: 5px !important;
	top: 0 !important;
}
#topVideoInfo div:not([class]) > .videoThumb {
	width: auto !important;
	height: 53px !important;
	border: 1px #000 solid !important;
	position: absolute !important;
	overflow: hidden !important;
}
#topVideoInfo div:not([class]) > .videoThumb .videoThumbnailImage {
	width: 92px !important;
	height: auto !important;
	margin: -8px 0 0 !important;
}
body.size_small #topVideoInfo .videoStats,
body.size_small #topVideoInfo div:not([class]) > .videoThumb {
	display: none !important;
}
body.size_medium #topVideoInfo .videoStats {
	bottom: -68px !important;
	right: 105px !important;
}
body.size_medium #topVideoInfo div:not([class]) > .videoThumb {
	bottom: -68px !important;
	right: 7px !important;
}
body.size_normal #topVideoInfo .videoStats {
	bottom: -68px !important;
	right: -7px !important;
}
body.size_normal #topVideoInfo div:not([class]) > .videoThumb {
	bottom: -68px !important;
	right: -106px !important;
}
/* タグエリア
----------------------------------------------- */

#videoTagContainer {
	min-width: 664px !important;
	max-width: 664px !important;
	padding: 0 !important;
	margin: 10px 0 0 !important;
}
#videoTagContainer.default:not(.isEdit):not(.active):not(:hover) {
	height: auto !important;
	overflow: auto !important;
}
#videoTagContainer.default:not(.isEdit) #videoHeaderTagList {
	height: 40px !important;
	overflow: auto !important;
}
#videoTagContainer.default:not(.isEdit):not(.active):hover,
#videoTagContainer.default.active,
#videoTagContainer.default:not(.isEdit):not(.active):hover #videoHeaderTagList,
#videoTagContainer.default.active:not(.isEdit) #videoHeaderTagList {
	min-height: 40px !important;
	height: auto !important;
}
#videoTagContainerPin {
	padding: 0 !important;
	display: none !important;
}
#videoTagContainer.default:not(.isEdit):hover #videoTagContainerPin {
	display: block !important;
}
#videoTagContainer.isEdit {
	border: 2px #ccc solid !important;
	padding-top: 10px !important;
}
#videoHeaderTagList > .moreTagsExist {
	display: none !important;
}
#videoTagContainer.isEdit #videoHeaderTagList > .videoHeaderTag {
	display: list-item !important;
}
#videoTagContainer.isEdit #videoHeaderTagList > .videoHeaderTag > .tagControlEditContainer {
	left: auto !important;
}
#videoTagContainer .tagInner {
	height: auto !important;
}
.tagControlContainer {
	padding: 0 !important;
}
#videoHeaderTagList .enable,
#videoHeaderTagList .disable {
	width: 13px !important;
	height: 13px !important;
}
#videoHeaderTagList {
	padding: 0 15px 0 90px !important;
}
#videoHeaderTagList:not(.tagEditDeny) .toggleTagEditInner {
	position: relative;
}
#videoTagContainer.tagEditDeny .toggleTagEditInner {
	color: #fff;
	font-size: 12px;
	line-height: 14px;
	background: #222;
	border-radius: 3px;
	padding: 3px 10px;
}
#videoHeaderTagList .videoHeaderTagEditLinkArea {
	background-color: #fff;
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
}
#videoHeaderTagList .videoHeaderTagEditLinkArea:before {
	content: '\00767b\009332\0030bf\0030b0';
	color: #fff;
	font-size: 12px;
	line-height: 14px;
	display: inline-block;
	background: #222;
	border-radius: 3px;
	margin: 0 0 5px;
	padding: 3px 10px;
}
#videoHeaderTagList > li.isCategory:before {
	content: '';
	width: 21px;
	height: 9px;
	background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABUAAAAJAgMAAACgbY/lAAAACVBMVEUAAADMAAD////l9xEdAAAAAXRSTlMAQObYZgAAADJJREFUCNdjEA0FAgYGIJEW6sAQFrYyFEhFLQ1dORPIS1sZCaJSp0KplZEOIJVAJVB9AGYXE3ZoaIhzAAAAAElFTkSuQmCC) no-repeat;
	margin-right: 5px;
	vertical-align: middle;
	display: inline-block;
}
#topVideoInfo .dicIcon .dic.enable:before,
#videoHeaderTagList li .tagControlContainer .dic span.enable {
	background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANBAMAAACAxflPAAAAD1BMVEUAAAAzMzP////MMzOZAABzMJ9rAAAAAXRSTlMAQObYZgAAAC9JREFUCNdjEAQDBgYhJSAQFoDyBRiEjYFABEgDhZVBtLERKl/J2EgZJx+uH2Y+AO+uCcpypIhGAAAAAElFTkSuQmCC) no-repeat !important;
}
#topVideoInfo .dicIcon .dic.disable:before,
#videoHeaderTagList li .tagControlContainer .dic span.disable {
	background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANBAMAAACAxflPAAAAD1BMVEUAAAAzMzOZmZn///9wcHDpMnUkAAAAAXRSTlMAQObYZgAAADRJREFUCNdjEAQDBgZhYyAQEoDyBRiElIBABEgrGxuDaWMlZSNkWkkZKA6mgeqg6jH5MPMB8H8JbfA8Wg4AAAAASUVORK5CYII=) no-repeat !important;
}
#videoHeaderTagList .videoHeaderTag {
	margin: 0 20px 0 0 !important;
	width: auto !important;
	height: auto !important;
	padding: 1px 4px 1px 0 !important;
	overflow: visible !important;
}
#videoHeaderTagList li .videoHeaderTagLink {
	text-decoration: underline !important;
}
#videoHeaderTagList li .videoHeaderTagLink:hover {
	text-decoration: none !important;
}
#videoHeaderTagList .toggleTagEdit {
	border-radius: 0 !important;
	margin: 0 !important;
	padding: 0 !important;
	background-color: transparent !important;
	height: auto !important;
}
#videoMenuWrapper {
	width: 300px !important;
	height: 36px !important;
	margin: 0 !important;
	overflow: visible !important;
	position: absolute !important;
	top: auto !important;
	bottom: 0 !important;
	right: 3px !important;
}
#videoMenuTopList {
	width: auto !important;
	margin: 0 !important;
	white-space: nowrap !important;
	right: -3px !important;
	bottom: 1px !important;
	background-color: #0d0d0d !important;
	padding: 0 8px !important;
	border-radius: 2px !important;
}
#videoMenuTopList > li {
	width: 40px !important;
	height: 36px !important;
	display: block !important;
	position: relative !important;
}
#videoMenuTopList .videoMenuListNicoru {
	display: block !important;
}
#videoMenuTopList > li > a {
	width: 100% !important;
	display: block !important;
}
#videoMenuTopList > li:not(.mymemoryButton):not(.videoMenuListNicoru):not(.menuAward):not(#speedChecker) > a {
	overflow: hidden !important;
}
#videoMenuTopList > li > a:hover {
	width: 120px !important;
	height: auto !important;
	color: #fff !important;
	font-size: 11px !important;
	line-height: 2em !important;
	white-space: nowrap !important;
	background: #2d2d2d !important;
	margin: -32px 0 0 -50px !important;
	padding: 0 10px !important;
}
#videoMenuTopList > li > a:hover:after {
	top: -12px;
	border: solid transparent;
	content: '';
	height: 0;
	width: 0;
	position: absolute;
	pointer-events: none;
	border-color: rgba(45, 45, 45, 0);
	border-top-color: #2d2d2d;
	border-width: 8px;
	left: 50%;
	margin-left: -8px;
}
#videoMenuTopList > li > a > span {
	margin: 0 !important;
	position: absolute !important;
	top: 0 !important;
	left: 0 !important;
}
#videoMenuTopList > li > form,
#videoMenuTopList > li > div {
	margin: 0 !important;
}
#videoMenuTopList > li > form + a,
#videoMenuTopList > li > div + a,
#videoMenuTopList > li.videoMenuList.menuUad > a > .nicoru-button {
	display: none !important;
}
#videoMenuTopList > li.mymemoryButton:hover > a,
#videoMenuTopList > li.videoMenuListNicoru:hover > a,
#videoMenuTopList > li.menuAward:hover > a,
#videoMenuTopList > li#speedChecker:hover > a {
	display: block !important;
	width: 120px !important;
