style.css:
@-moz-document domain("splusdev.tk") {
    #container > header {
        background-color: #003D51 !important;
    }
    nav {
        background-color: #1F1F1F !important;
    }
    nav a {
        background-color: #555 !important;
    }
    nav li:hover > a, nav .current, nav a:active{
        background-color: #275F74 !important;
    }
    #social a:hover {
        background-color: #333 !important;
    }
    #main-content {
        background-color: #111 !important;
        color: #fff !important;
    }
    article #header {
        border-color: #555 !important;
    }
    article #header h3 {
        color: #ECF0F1 !important;
    }    
    article.odd {
        background-color: #333 !important;
    }
    time.normal {
        background-color: #333 !important;
    }
    article.odd time.normal {
        background-color: #4E4E4E !important;
    }
    article a {
        color: #5C5C5C !important;
    }
    article a:hover {
        color: #275F74 !important;
    }
    a.thread_link {
        background-color: #5C5C5C !important;
        color: #fff !important;
    }
    a.thread_link:hover {
        background-color: #275F74 !important;
        color: #fff !important;
    }
    
    div.result {
        color: black !important;
    }
}
