style.css:
@-moz-document regexp("http://www\\.nicovideo\\.jp/watch/(?!.*\\?edit=owner).*") {
/* 幅指定
======================================== */
#siteHeader #siteHeaderInner,
#videoHeader,
body:not(.videoExplorer):not(.full_with_browser) #playerAlignmentArea,
body:not(.videoExplorer) #playlist,
#outline .outer {
  width: auto !important;
  max-width: 1008px !important;
}
#playerAlignmentArea {
  width: auto !important;
}
#siteHeader #siteHeaderInner {
  min-width: 880px !important;
}
#videoHeader {
  min-width: 760px !important;
}
#playerAlignmentArea.size_medium {
  min-width: 1008px !important;
}
#outline .outer {
  min-width: 880px !important;
}

/* 動画プレイヤー(大)
---------------------------------------- */
body.size_normal #siteHeader #siteHeaderInner,
body.size_normal #videoHeader,
body:not(.full_with_browser) #playerAlignmentArea.size_normal,
body.size_normal #playlist,
body.size_normal #outline .outer {
  max-width: 1234px !important;
}
body:not(.full_with_browser) #playerAlignmentArea.size_normal {
  min-width: 1234px !important;
}

/* サイトヘッダー
======================================== */
#siteHeader {
  box-shadow: 0 0 10px rgba(0,0,0,.8);
}

/* 再生終了後のスクロールを無効化
======================================== */
#videoHeader.menuOpened .tvChanMenuHeightController,
#videoHeader.menuOpened #editorMenu {
  margin: 0 !important;
}

/* ヘッダーコンテンツ
======================================== */
#videoHeader {
  padding: 0 !important;
}
#videoHeader #videoHeaderDetail {
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  max-width: calc(100% - 330px) !important;
  padding-top: 24px !important;
  height: 64px !important;
}
#videoHeader.infoActive #videoHeaderDetail {
  padding-left: 100px !important;
  padding-top: 36px !important;
}
#videoHeaderDetail.active .videoDetailExpand {
  padding: 0 !important;
  height: auto !important;
}
#videoHeaderDetail .ch_breadcrumb {
  display: none !important;
}
#videoHeaderDetail .videoDetailExpand {
  display: block !important;
}
#videoHeaderDetail h2 {
  display: block !important;
  width: 100% !important;
  max-width: 100% !important;
  vertical-align: baseline;
  white-space: nowrap !important;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 140% !important;
}
#videoHeaderDetail h2>span {
  font-size: inherit !important;
}
/* 動画再生終了後の位置ずれ無効化 */
#videoHeader.menuOpened #videoHeaderDetail,
#videoHeader.menuClosed #videoHeaderDetail {
  margin-top: 0 !important;
}

/* 上部エリア
---------------------------------------- */
#topVideoInfo {
  margin-bottom: 6px !important; 
}

/* 縮小 動画説明文
---------------------------------------- */
#videoHeader .shortVideoInfo {
  margin: 0 !important;
}
#videoHeader .shortVideoInfo .shortDescription {
  margin: 0 !important;
  width: calc(100% - 280px) !important;
}

/* 動画説明文
---------------------------------------- */
#videoDetailInformation .description {
  border: 2px solid #3a3a3a;
  border-radius: 2px;
  min-height: 88px !important;
  padding: 10px !important;

  font-size: 90%;
  line-height: 1.4;
  margin-bottom: 0 !important;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
}
#videoDetailInformation .description,
div.commonsTreeExists #topVideoInfo .parentVideoInfo {
  width: calc(100% - 334px) !important;
}
#videoDetailInformation .description a{
  text-decoration: underline !important;
}

/* チャンネルプロファイル
---------------------------------------- */
body.channel .ch_prof p.bread {
  margin: 0 !important;
  line-height: 1.2;
}

/* 動画情報
---------------------------------------- */
#videoDetailInformation .videoMainInfoContainer {
  padding: 0 !important;
  border: 0 !important;
}
#topVideoInfo .videoMainInfoContainer>div:nth-child(2) {
  margin-top: 5px !important;
}
#topVideoInfo .videoMainInfoContainer>div:nth-child(2) .videoThumb {
  position: absolute;
  top: 10px;
}
#topVideoInfo .videoMainInfoContainer>div:nth-child(2) .videoThumb .videoThumbnailImage {
  width: 88px !important;
  height: 66px !important;
  border: 1px solid #000000;
  border-radius: 2px;
}
#topVideoInfo .infoHeadOuter {
  position: absolute;
  top: 15px;
  left: 100px;
}
#topVideoInfo .videoMainInfoContainer .hiddenInfo {
  margin: 0 !important;
}
#topVideoInfo .hiddenInfo .extraVideoInfo,
#topVideoInfo .hiddenInfo .videoEditMenu .videoEditMenuLinks,
#topVideoInfo .hiddenInfo .videoShareBody,
#topVideoInfo .hiddenInfo .expandContainer {
  background: transparent !important;
}
div.commonsTreeExists #topVideoInfo .parentVideoInfo {
  float: none !important;
  margin: -2px 0 0 !important;
  border: 2px solid #3a3a3a !important;
  border-top: 1px dashed #aaaaaa !important;
  border-radius: 0 0 2px 2px;
  background: #f3f3f3 !important;
  padding: 6px 10px !important;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
}
#topVideoInfo .parentVideoInfo .cct {
  margin-bottom: 5px !important;
}
#topVideoInfo .parentVideoInfo .videoThumb img {
  border: 1px solid #aaaaaa !important;
}
#topVideoInfo .ranking {
  position: absolute;
  top: 58px;
  left: 100px;
  border: 0 !important;
}
#topVideoInfo .dicIcon {
  display: block;
  position: absolute;
  top: 63px;
  right: 140px;
}
#topVideoInfo {
  margin-bottom: 0 !important;
}
#topVideoInfo .videoMainInfoContainer .hiddenInfo {
  margin-top: 4px !important;
}
#topVideoInfo .videoMainInfoContainer div:nth-child(3) {
  margin-top: 0 !important;
}
#topVideoInfo .hiddenInfoTabHeader {
  overflow: hidden !important;
}
#topVideoInfo .hiddenInfo .extraVideoInfo {
  padding: 0 8px !important;
}
/* 非公開動画 */
#topVideoInfo .videoMainInfoContainer .hiddenUserProfile {
  margin: -2px 0 0 !important;
  width: calc(100% - 324px) !important;
  border: 2px solid #3a3a3a !important;
  border-top: 1px dashed #aaaaaa !important;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
}

/* 投稿者用メニュー表示時の調整 (72px) */
#videoHeader.ownerThreadEditor.menuOpened #editorMenu {
  margin-top: 12px !important;
  margin-bottom: 20px !important;
}
#videoHeader.ownerThreadEditor #topVideoInfo .videoMainInfoContainer>div:nth-child(2) .videoThumb {
  top: 72px;
}
#videoHeader.ownerThreadEditor #topVideoInfo .infoHeadOuter {
  top: calc(72px + 5px);
}
#videoHeader.ownerThreadEditor #topVideoInfo .ranking {
  top: calc(72px + 48px);
}
#videoHeader.ownerThreadEditor #topVideoInfo .dicIcon {
  top: calc(72px + 53px);
}

/* タグ
---------------------------------------- */
#videoTagContainer {
  min-width: 0 !important;
  min-height: 41px !important;
  margin: 0 !important;
  padding: 0 !important;
  margin-bottom: 2px !important;
  float: left;
  width: calc(100% - 40px * 7 - 73px) !important;
  height: auto !important;
  overflow: visible !important;
}
#videoHeader.noAward #videoTagContainer,
#videoHeader.noAudioDownload #videoTagContainer {
  width: calc(100% - 40px * 6 - 73px) !important;
}
#videoHeader.noAward.noAudioDownload #videoTagContainer {
  width: calc(100% - 40px * 5 - 73px) !important;
}
#videoTagContainer .tagInner {
  background-color: rgba(244,244,244,.5) !important;
  border-radius: 4px;
  overflow: visible !important;
}
#videoTagContainer.default .tagInner {
  height: auto !important;
}
#videoHeaderTagList {
  padding-left: 80px !important;
}
#videoHeaderTagList .toggleTagEdit {
  height: auto !important;
  padding: 0 !important;
  display: block !important;
  background: transparent !important;
  font-size: 90%;
  width: 6em !important;
}
#videoHeaderTagList .toggleTagEdit .toggleTagEditInner {
  display: block !important;
  text-align: center;
  border: 1px solid #292f2f;
  border-radius: 2px;
  background: linear-gradient(to bottom, #47474d, #38383e);
  color: #ffffff;
  line-height: 20px;
}
#videoHeaderTagList .toggleTagEdit span.videoHeaderTagEditLinkArea {
  display: block;
  position: absolute;
  top: 22px;
  left: 0;
  width: 6.2em;
  text-align: center;
}
#videoHeaderTagList li {
  margin: 0 !important;
  padding: 0 !important;
  display: block !important;
  float: left;
  height: 20px !important;
}
#videoHeaderTagList li a {
  text-decoration: underline !important;
}
#videoHeaderTagList li .tagControlContainer,
#videoHeaderTagList li .tagControlEditContainer {
  position: static !important;
  padding: 0 4px !important;
}
#videoTagContainer:not(.isEdit) #videoHeaderTagList .tagControlContainer {
  display: inline-block !important;
  top: 0;
}
#videoHeaderTagList li .tagControlContainer .nicoruWrapper {
  position: absolute;
}
#tagEditContainer {
  margin-left: 76px !important;
  padding: 1px 0 4px !important; 
}
#videoTagContainer.isEdit #videoHeaderTagList li .tagControlContainer .nicoruWrapper {
  display: none !important;
}
#videoTagContainer .tagInner #videoTagContainerPin.active {
  display: none;
}

/* 動画メニューボタン
---------------------------------------- */
#videoMenuWrapper {
  height: auto !important;
  margin: 1px 0 2px !important;
  float: right;
  overflow: visible !important;
  width: auto !important;
}
#videoMenuTopList {
  width: auto !important;
  margin: 0 10px 0 0 !important;
  padding: 0 !important;
  direction: rtl;
}
#videoMenuTopList li.videoMenuList,
#videoMenuTopList li.videoMenuListNicoru {
  display: inline-block !important;
  width: 4em !important;
  font-size: 10px;
  float: none !important;
  vertical-align: top;
}
#videoHeader.noAudioDownload #videoMenuTopList li.videoMenuList:nth-child(6) {
  display: none !important;
}
#videoMenuTopList {
  position: static !important;
}
#videoMenuTopList li.videoMenuList a {
  text-decoration: none !important;
}
#videoMenuTopList li.videoMenuList a:hover,
#videoMenuTopList li.videoMenuListNicoru a:hover{
  text-decoration: underline !important;
}
#videoMenuTopList li.videoMenuList:nth-child(1),
#videoMenuTopList li.videoMenuList.menuAward {
  width: 5em !important;
}

/* メニューボタンの説明を隠すON/OFF */
#videoHeader:not(.infoActive) #videoMenuTopList > li {
  height: 36px !important;
  overflow: hidden !important;
}

/* マウスオーバー時にツールチップを表示 */
#videoHeader:not(.infoActive) #videoMenuTopList > li {
  position: relative;
}
#videoHeader:not(.infoActive) #videoMenuTopList > li:hover:after {
  position: absolute;
  top: calc(50% - 1em - 2px);
  left: 0;
  padding: 2px 0;
  font-size: 10px;
  width: 4em;
  background: rgba(244,244,244,.84);
  pointer-events: none;
}
#videoHeader:not(.infoActive) #videoMenuTopList > li:first-child:hover:after {
  width: 5em;
  content: "とりあえずマイリスト";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li:nth-of-type(2):hover:after {
  content: "マイリスト";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li:nth-of-type(3):hover:after {
  content: "ニコニ広告で宣伝";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li:nth-of-type(4):hover:after {
  content: "マイメモリー";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li.videoMenuListNicoru:hover:after {
  top: calc(50% - 0.5em - 2px);
  content: "ニコる";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li:nth-of-type(6):hover:after {
  content: "音楽ダウンロード";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li.menuAward:hover:after {
  width: 5em;
  content: "動画アワードへ投票";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li.speedChecker:hover:after {
  content: "スピードチェック";
}
#videoHeader:not(.infoActive) #videoMenuTopList > li.userChennel:hover:after {
  content: "投稿者を推薦";
}

#videoMenuTopList li .nicoruButton .nicoru-button .nicorub-link .nicorub-balloon {
  margin-right: -12px !important;
}
#videoHeader.noAward .menuAward {
  display: none !important;
}
#videoHeader a.videoMenuToggleButton {
  display: none !important;
}
/* "投稿者を推薦"を非表示に */
#videoMenuTopList li.videoMenuList.userChannel {
  display: none !important;
}

/* 動画プレイヤー
======================================== */
body:not(.videoExplorer) #playerContainerWrapper {
  padding: 3px 0 5px !important;
}
body.size_small #playerContainer.setting_panel #nicoplayerContainerInner,
body.size_small #playerContainer.setting_panel #nicoplayerContainerInner object {
  width: 340px !important;
  height: 191px !important;
}
body:not(.full_with_browser) #playerAlignmentArea:not(.size_small) #playerContainer.controll_panel.oldTypeCommentInput {
  height: auto !important;
}
#playerSettingPanel {
  margin-left: 350px !important;
}
#playerNicoplayer {
  position: static !important;
}

/* 右カラム
---------------------------------------- */
body:not(.videoExplorer):not(.setting_panel):not(.full_with_browser) #playerTabWrapper {
  height: auto !important;
  max-height: 5000px;
  bottom: 0;
}
#playerTabContainer .playerTabHeader,
#playerTabContainer .playerTabHeader .playerTabItem {
  height: 24px !important;
  line-height: 1.4 !important;
}
#playerTabContainer .playerTabContent{
  top: 24px !important;
  padding: 5px !important;
}
#playerTabContainer .playerTabAds {
  padding: 0 !important;
  margin: 10px auto !important;
}
#playerCommentPanel .commentTableContainer {
  width: 100% !important;
}

/* 右カラム 共有ボタンの非表示
---------------------------------------- */
#playerTabContainer .socialButtons{display: none!important;}

/* 右カラム ニコメンド
---------------------------------------- */
#nicommendPanel .nicommendContentsOuter {
  padding: 5px 0;
}
#nicommendPanel .contentHeader.empty,
#nicommendPanel .empty img {
  display: none;
}
#nicommendPanel .contentHeader.assist,
#nicommendPanel .contentHeader.priority,
#nicommendPanel .contentHeader.default {
  padding: 4px 5px !important;
  margin: 0 5px;
}
#nicommendPanel .contentHeader.priority {
  border: #d7b55b solid 1px !important;
}

/* プレイリスト
======================================== */
#playlist  {
  margin-left: auto !important;
  margin-right: auto !important;
  overflow: hidden;
  background-color: transparent !important;
}
#playlistContainer .playlistItem .balloon {
  padding: 0 6px !important;
}
#playlistContainer .playlistItem .balloon .videoInformation {
  margin: 0 !important;
}

#playlist .playlistInformation {
  box-shadow: none !important;
  background: linear-gradient(to bottom, rgba(51,51,51,.75), rgba(70,70,70,.75)) !important;
  border: 1px solid rgba(63,63,63,.75);
  border-bottom: 0;
  padding-top: 0 !important;
  padding-bottom: 2px !important;
}
#playlist #playlistContainer {
  background-color: rgba(31,31,31,.75) !important;
}
#playlistContainer .playlistItem {
  opacity: .8 !important;
}
#playlistContainer .playlistItem:not(.playing) a:not(:hover) {
  background: none !important;
}
#playlistContainer .playlistItem.empty {
  box-shadow: none !important;
}

body.videoExplorer #playlist {
  background-color: #3c3c3c;
}
body.videoExplorer #playlist .playlistInformation {
  box-shadow: inset 0 2px 4px #141414;
}

/* フッターコンテンツ
======================================== */
#outline .main {
  width: calc(100% - 352px) !important;
}

/* 動画情報
---------------------------------------- */
#videoInfo div.videoMainInfoContainer {
  /*display: block !important;*/
  margin: 0 0 10px !important;
  padding: 0 !important;
  border: 0 !important;
}
#videoInfo div.videoMainInfoContainer>div:not(#videoShareLinks) {
  display: none !important;
}
#videoInfo #videoShareLinks {
  margin: 0 !important;
}

/* ニコメンドリスト
---------------------------------------- */
#nicommendList {
  margin-top: 10px !important;
}
#nicommendList>img:nth-child(1) {
  display: none;
}
#nicommendList .content .column.balloon {
  width: calc(100% - 98px) !important;
}

/* ニコニコ市場
---------------------------------------- */
#ichibaMain {
  width: 100%;
  display: flex;
  flex-flow: row wrap;
}
#ichibaMain #ichibaMainLogo {
  position: static;
  width: 100%;
  margin: 0 0 10px;
  font-size: 14px;
  text-shadow: none;
}
#ichibaMain #ichibaMainLogo a {
  color: #001835;
  font-weight: 700;
}
#ichibaMain #ichibaMainHeader {
  display: none;
}
#ichibaMain dl {
  float: none;
}
#ichibaMain dl:not(.pia) {
  width: 154px;
  height: auto;
  overflow: hidden;
  margin: 0 12px 10px 0;
}
#ichibaMain dl.pia { /* チケットぴあ */
  width: 100%;
}
#ichibaMain p.noItem {
  width: 100%;
}
#ichibaMain .rowJustify {
  display: none;
}
/* サムネイル */
#ichibaMain dd.thumbnail img:not(.lock) {
  width: 100%;
  height: 100%;
  border: 0;
  object-fit: contain;
}
/* 着ボイス等 */
#ichibaMain dd.mobile {
  border: 1px solid #cccccc;
  border-bottom: 0;
  width: 152px !important;
  box-sizing: border-box;
}
#ichibaMain dd.mobile a {
  background-position: -18px -576px !important;
}
#ichibaMain .thumbnail a {
  font-family: "メイリオ", san-serif;
  line-height: 1;
}
#ichibaMain .thumbnail a>span {
  font-size: 120% !important;
}
#ichibaMain dd.mobile + dd.thumbnail {
  border-top: 0;
  height: 130px !important;
  padding: 2px 4px;
}
/* ニコニコチャンネル */
#ichibaMain dd.thumbnail,
#ichibaMain dd.blomaga,
#ichibaMain dd.blomagaArticle,
#ichibaMain dd.blomagaArticleNP {
  width: 152px;
  height: 152px;
  box-sizing: border-box;
  border: 1px solid #cccccc;
  margin: 0 0 3px !important;
}
#ichibaMain dd.blomaga,
#ichibaMain dd.blomagaArticle,
#ichibaMain dd.blomagaArticleNP {
  background: none;
  padding: 2px 4px;
}
#ichibaMain dd.blomaga a,
#ichibaMain dd.blomagaArticle a,
#ichibaMain dd.blomagaArticleNP a {
  width: 100% !important;
  height: 100% !important;
}
#ichibaMain dd.blomaga p.blomagaThumbnail {
  padding: 0;
  height: calc(100% - 14px);
}
#ichibaMain dd.blomaga p.blomagaLogo,
#ichibaMain dd.blomagaArticle p.blomagaLogo,
#ichibaMain dd.blomagaArticle p.blomagaThumbnail,
#ichibaMain dd.blomagaArticle p.blomagaTitle,
#ichibaMain dd.blomagaArticle p.blomagaAuthor,
#ichibaMain dd.blomagaArticleNP p.blomagaLogo,
#ichibaMain dd.blomagaArticleNP p.blomagaThumbnail,
#ichibaMain dd.blomagaArticleNP p.blomagaTitle,
#ichibaMain dd.blomagaArticleNP p.blomagaAuthor {
  padding: 0 !important;
  height: auto !important;
}
#ichibaMain dd.blomagaArticle p.blomagaThumbnail {
  margin: 5px 0 !important;
}
#ichibaMain dd.blomagaArticle p.blomagaTitle {
  font-size: 10px !important;
  line-height: 1.2 !important;
  margin: 5px 0 !important;
}
#ichibaMain dd.blomaga img,
#ichibaMain dd.blomagaArticle img {
  width: 100%;
  height: 100%;
  border: 0;
  object-fit: contain;
}
#ichibaMain dd.blomagaArticleNP p.blomagaText {
  background: #f6f4ed;
  border: 2px solid #e4e4e4;
  border-radius: 4px;
  margin: 4px 0 2px;
}
/* 商品名以下 */
#ichibaMain dd.itemname {
  height: 2.2em;
  line-height: 1.2;
  overflow: hidden;
}
#ichibaMain dd.maker {
  white-space: nowrap;
  text-overflow: ellipsis;
}
/* フッター */
#ichibaMain #ichibaMainFooter {
  width: 100%;
}
#ichibaMain #ichibaMainFooter .commandArea {
  margin: 10px 0;
}
#ichibaMain #ichibaMainFooter .commandArea input {
  margin: 0;
}

/* 公告に関する微調整
======================================== */
#playerBottomAd {
  margin-bottom: 16px !important;
}
#videoReview {
  margin: 0 !important;
}

/* "動画をもっと見る"画面
======================================== */
/* 再生リスト・動画プレイヤー
---------------------------------------- */
body.size_small.no_setting_panel.videoExplorer #content,
#videoExplorer {
  width: auto !important;
  max-width: 1024px !important;
}
body.size_small.no_setting_panel.videoExplorer #content {
  position: fixed !important;
  top: 36px !important;
  left: 0 !important;
  right: 0 !important;
  margin: 0 auto !important;
  height: auto !important;
  box-shadow: 0 2px 4px rgba(0,0,0,.5) !important;
  padding: 0 !important;
}
body.size_small.no_setting_panel.videoExplorer #playlist {
  position: static !important;
  background-color: #f4f4f4 !important;
}
body.size_small.no_setting_panel.videoExplorer #playlist>div {
  width: 100% !important;
}
body.size_small.no_setting_panel.videoExplorer #content .videoHeaderOuter {
  display: none;
}
body.size_small.no_setting_panel.videoExplorer #playerContainerWrapper {
  position: absolute !important;
  top: 44px !important;
  bottom: auto !important;
  left: 0 !important;
  right: auto !important;
  height: auto !important;
  z-index: auto !important;
  box-shadow: 0px 2px 4px rgba(0,0,0,.5) !important;
}
body.size_small.no_setting_panel.videoExplorer.playlist #playerContainerWrapper {
  top: 174px !important;
}
body.size_small.no_setting_panel.videoExplorer #playerAlignmentArea.size_small {
  margin: 0 !important;
  height: auto !important;
}
body.size_small.no_setting_panel.videoExplorer #playerContainer.controll_panel.oldTypeCommentInput #nicoplayerContainer,
body.size_small.no_setting_panel.videoExplorer #playerContainer.controll_panel.oldTypeCommentInput #external_nicoplayer {
  height: 160px !important;
}
body.size_small.no_setting_panel.videoExplorer:not(.playlist) #playlistContainer,
body.size_small.no_setting_panel.videoExplorer:not(.playlist) div.playlistTrashbox
{
  display: none;
}

/* フッターコンテンツ
---------------------------------------- */
#videoExplorer {
  position: static !important;
  margin: auto !important;
  background: transparent !important;
  padding-top: 70px !important;
}
body.playlist #videoExplorer {
  padding-top: 200px !important;
}
#videoExplorer .videoExplorerMenu {
  position: fixed !important;
  top: auto !important;
  left: auto !important;
  margin-top: 200px !important;
}
