style.css:
@-moz-document regexp("https?://www\\.mediafire\\.com/file/.*") {

/*
Check out my other stuff
https://userstyles.org/users/179885
*/

/*align footer bottom*/
body {
  background: transparent !important;
  display: flex !important;
  flex-direction: column;
}
body #container {
  min-height: 500px !important;
}
body #container #content_container {
  padding: 0 !important;
}
body #container #content_container #download_visible_wrapper {
  height: auto !important;
}
body #footer {
  margin-top: auto !important;
}

/*download link and info in the middle*/
.content {
  width: auto !important;
}
.center {
  float: none !important;
  background: none !important;
  position: relative !important;
  margin-left: auto !important;
  margin-right: auto !important;
  width: 450px !important;
  height: auto !important;
  min-height: 450px !important;
}

/* dark download box container */
.promo-dl-box {
  width: 430px !important;
}
.promo-dl-box .dl-btn-container {
  background: #292929;
  padding: 14px 15px 14px 14px !important;
  border-radius: 6px !important;
  height: 150px !important;
}
.promo-dl-box .dl-btn-container .promoDownloadName {
  left: 71px !important;
  width: calc(100% - 90px) !important;
  margin-top: -7px !important;
}
.promo-dl-box .dl-btn-container .dl-btn-label {
  width: calc(100% - 0px) !important;
}

/* green DL button */
.promo-dl-box .dl-btn-container .download_link {
  position: absolute !important;
  top: auto !important;
  bottom: 15px !important;
  left: 17px !important;
  width: 395px !important;
}
.promo-dl-box .dl-btn-container .download_link,
.promo-dl-box .dl-btn-container .download_link a {
  height: 50px; line-height: 52px;
}
.promo-dl-box .dl-btn-container .download_link a,
.promo-dl-box .dl-btn-container .dlPreviewLink {
  background-color: #6CA117 !important;
  background-image: linear-gradient(#7EBA1B,#6CA117) !important;
}

/* preview button */
.promo-dl-box .dl-btn-container .dlPreviewLink {
  right: auto !important; top: auto !important;
  left: 17px !important; bottom: 16px !important;
}
.promo-dl-box .dl-btn-container .dlPreviewLink ~ .download_link {
  width: 322px !important;
  left: 90px !important;
}

/* file information */
.dlInfo {
  top: 180px !important;
  padding: 15px 20px !important;
}
.dlInfo .dlInfo-Intro > div,
.dlInfo .dlInfo-Details {
  margin-right: 0 !important;
}

/* remove unnecessary stuff */

#download-dark-screen,
.nonDLContentWrap,
.promo-dl-box .dlPromoContent, 
.promo-dl-box .dlInfo-Sidebar,
.promo-dl-box .dlInfo-Body,
iframe#ads,
.DLExtraInfo-wrap,
.socialContainerWrap,
.DLMobile-upsellBox {
  display: none !important;
  background: #F8F8F8;
}

}

@-moz-document domain("mediafire.com") {
/* needed because userstyles can't determine which site the style affects... -_- */
}
