style.css:
@-moz-document domain("goodreads.com") {

/* Saeed Habibi, Twitter: @saeedhbi */

   * {
    font-family: tahoma !important;
    font-size: 8pt !important;
}

.quoteText, .reviewText, .infoBoxRowItem, #description, .readable, .authorName, .bookTitle, #comment_body_usertext, .reviewUserText, #book_description_defaulted {
    text-align: justify !important;
    direction: rtl !important;
    font-size: 8pt !important;
}

span[id^="freeTextreview"],span[id^="freeTextContainer"],span[id^="freeText"] {
    text-align: justify !important;
    line-height: 18pt;
    float: right;
    direction: rtl;
}

.otherEditionCovers {
    width: 340px !important;
}

.staticStars {
    text-indent: 1000em;
}

.reviewText {
    line-height: 17pt !important;
}

#self_serve_ads, div[id^="div-gpt-ad"] {
    display: none;
}

.readMoreLink {
    float: none;
}

.updateBodyAfterImage {
    text-align: justify;
    direction: rtl;
    line-height: 22px;
}

div[class^="infoBoxRow"] {
    direction: ltr !important;
}
        
}
